import React, { useState, useEffect } from 'react';
import { apiService, dataHelpers, handleApiError, handleApiSuccess } from '../services/api';

const DoctorDashboard = () => {
    const [currentTime, setCurrentTime] = useState(new Date());
    const [appointments, setAppointments] = useState([]);
    const [todayAppointments, setTodayAppointments] = useState([]);
    const [patients, setPatients] = useState([]);
    const [doctors, setDoctors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [stats, setStats] = useState({
        todayPatients: 0,
        totalPatients: 0,
        pendingAppointments: 0,
        completedToday: 0
    });

    // Update time every minute
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 60000);
        return () => clearInterval(timer);
    }, []);

    // Fetch data from database
    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        setLoading(true);
        setError(null);
        
        try {
            // Fetch all necessary data in parallel
            const [appointmentsRes, patientsRes, doctorsRes] = await Promise.all([
                apiService.getAppointments(),
                apiService.getPatients(),
                apiService.getDoctors()
            ]);

            const appointmentsData = handleApiSuccess(appointmentsRes).data;
            const patientsData = handleApiSuccess(patientsRes).data;
            const doctorsData = handleApiSuccess(doctorsRes).data;

            // Process appointments with patient names
            const processedAppointments = appointmentsData.map(appointment => {
                const patient = patientsData.find(p => p.id === appointment.patient_id);
                const doctor = doctorsData.find(d => d.id === appointment.doctor_id);
                
                return {
                    ...appointment,
                    patientName: patient ? patient.full_name : 'Không xác định',
                    doctorName: doctor ? doctor.full_name : 'Không xác định',
                    doctorDepartment: doctor ? doctor.department : 'Không xác định'
                };
            });

            // Filter today's appointments
            const today = dataHelpers.getTodayDate();
            const todayAppts = dataHelpers.filterAppointmentsByDate(processedAppointments, today);

            // Calculate statistics
            const appointmentStats = dataHelpers.getAppointmentStats(appointmentsData);
            const uniquePatientIds = new Set(appointmentsData.map(apt => apt.patient_id));

            setAppointments(processedAppointments);
            setTodayAppointments(todayAppts);
            setPatients(patientsData);
            setDoctors(doctorsData);
            setStats({
                todayPatients: todayAppts.length,
                totalPatients: patientsData.length,
                pendingAppointments: appointmentStats.todayPending,
                completedToday: appointmentStats.todayCompleted
            });

        } catch (err) {
            const errorResult = handleApiError(err);
            setError(errorResult.message);
            console.error('Failed to fetch dashboard data:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleUpdateAppointmentStatus = async (appointmentId, newStatus) => {
        try {
            await apiService.updateAppointmentStatus(appointmentId, newStatus);
            // Refresh data after update
            fetchDashboardData();
            alert(`Cập nhật trạng thái thành công: ${dataHelpers.getStatusText(newStatus)}`);
        } catch (err) {
            const errorResult = handleApiError(err);
            alert(`Lỗi cập nhật: ${errorResult.message}`);
        }
    };

    const formatTime = (date) => {
        return date.toLocaleTimeString('vi-VN', { 
            hour: '2-digit', 
            minute: '2-digit',
            hour12: false 
        });
    };

    const formatDate = (date) => {
        return date.toLocaleDateString('vi-VN', { 
            weekday: 'long',
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
        });
    };

    if (loading) {
        return (
            <div style={{ backgroundColor: '#f8fafc', minHeight: '100vh' }}>
                <div className="container-fluid py-4">
                    <div className="text-center">
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Đang tải...</span>
                        </div>
                        <p className="mt-3 text-muted">Đang tải dữ liệu dashboard...</p>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ backgroundColor: '#f8fafc', minHeight: '100vh' }}>
                <div className="container-fluid py-4">
                    <div className="alert alert-danger text-center">
                        <h4>⚠️ Lỗi kết nối</h4>
                        <p>{error}</p>
                        <button 
                            className="btn btn-primary" 
                            onClick={fetchDashboardData}
                        >
                            <i className="fas fa-redo me-2"></i>
                            Thử lại
                        </button>
                        <div className="mt-3">
                            <small className="text-muted">
                                💡 Hướng dẫn: Chạy lệnh <code>npx json-server database.json --port 9999</code> để khởi động database
                            </small>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div style={{ backgroundColor: '#f8fafc', minHeight: '100vh' }}>
            {/* Bootstrap CSS */}
            <link 
                href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" 
                rel="stylesheet"
            />
            <link 
                href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css" 
                rel="stylesheet"
            />

            <div className="container-fluid py-4">
                {/* Header Section */}
                <div className="row mb-4">
                    <div className="col-12">
                        <div className="d-flex justify-content-between align-items-center">
                            <div>
                                <h1 className="h3 fw-bold mb-1" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-user-md me-3" style={{ color: '#3b82f6' }}></i>
                                    Doctor Dashboard
                                </h1>
                                <p className="text-muted mb-0">
                                    <i className="fas fa-calendar-day me-2"></i>
                                    {formatDate(currentTime)} • Dữ liệu thời gian thực
                                </p>
                            </div>
                            <div className="text-end">
                                <div className="h4 fw-bold mb-0" style={{ color: '#3b82f6' }}>
                                    <i className="fas fa-clock me-2"></i>
                                    {formatTime(currentTime)}
                                </div>
                                <button 
                                    className="btn btn-sm btn-outline-primary"
                                    onClick={fetchDashboardData}
                                    disabled={loading}
                                >
                                    <i className="fas fa-sync-alt me-1"></i>
                                    Làm mới
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Stats Cards */}
                <div className="row mb-4">
                    {[
                        {
                            title: 'Lịch hẹn hôm nay',
                            value: stats.todayPatients,
                            icon: 'fa-calendar-check',
                            color: '#3b82f6',
                            bgColor: '#dbeafe'
                        },
                        {
                            title: 'Tổng bệnh nhân',
                            value: stats.totalPatients,
                            icon: 'fa-users',
                            color: '#10b981',
                            bgColor: '#d1fae5'
                        },
                        {
                            title: 'Chờ xác nhận',
                            value: stats.pendingAppointments,
                            icon: 'fa-clock',
                            color: '#f59e0b',
                            bgColor: '#fef3c7'
                        },
                        {
                            title: 'Hoàn thành hôm nay',
                            value: stats.completedToday,
                            icon: 'fa-check-circle',
                            color: '#10b981',
                            bgColor: '#d1fae5'
                        }
                    ].map((stat, index) => (
                        <div key={index} className="col-xl-3 col-md-6 mb-3">
                            <div className="card border-0 rounded-4 h-100" style={{
                                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
                                transition: 'all 0.3s ease'
                            }}>
                                <div className="card-body p-4">
                                    <div className="d-flex align-items-center">
                                        <div className="rounded-circle d-flex align-items-center justify-content-center me-3" style={{
                                            width: '60px',
                                            height: '60px',
                                            backgroundColor: stat.bgColor
                                        }}>
                                            <i className={`fas ${stat.icon} fs-4`} style={{ color: stat.color }}></i>
                                        </div>
                                        <div>
                                            <div className="h3 fw-bold mb-1" style={{ color: stat.color }}>
                                                {stat.value}
                                            </div>
                                            <div className="small text-muted fw-semibold">{stat.title}</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="row">
                    {/* Today's Appointments */}
                    <div className="col-lg-8 mb-4">
                        <div className="card border-0 rounded-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <div className="d-flex justify-content-between align-items-center">
                                    <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                        <i className="fas fa-calendar-check me-2" style={{ color: '#3b82f6' }}></i>
                                        Lịch hẹn hôm nay ({todayAppointments.length})
                                    </h5>
                                    <button className="btn btn-primary btn-sm rounded-pill">
                                        <i className="fas fa-plus me-2"></i>Thêm lịch hẹn
                                    </button>
                                </div>
                            </div>
                            <div className="card-body p-0">
                                {todayAppointments.length === 0 ? (
                                    <div className="text-center py-5">
                                        <i className="fas fa-calendar-times text-muted fs-1 mb-3"></i>
                                        <h6 className="text-muted">Không có lịch hẹn nào hôm nay</h6>
                                    </div>
                                ) : (
                                    <div className="table-responsive">
                                        <table className="table table-hover mb-0">
                                            <thead style={{ backgroundColor: '#f8fafc' }}>
                                                <tr>
                                                    <th className="border-0 px-4 py-3 fw-semibold">Thời gian</th>
                                                    <th className="border-0 px-4 py-3 fw-semibold">Bệnh nhân</th>
                                                    <th className="border-0 px-4 py-3 fw-semibold">Ghi chú</th>
                                                    <th className="border-0 px-4 py-3 fw-semibold">Trạng thái</th>
                                                    <th className="border-0 px-4 py-3 fw-semibold">Thao tác</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {todayAppointments.map((appointment) => {
                                                    const dateTime = dataHelpers.formatDateTime(appointment.appointment_datetime);
                                                    return (
                                                        <tr key={appointment.id}>
                                                            <td className="px-4 py-3">
                                                                <div className="fw-semibold" style={{ color: '#1f2937' }}>
                                                                    {dateTime.time}
                                                                </div>
                                                                <small className="text-muted">{appointment.shift}</small>
                                                            </td>
                                                            <td className="px-4 py-3">
                                                                <div className="d-flex align-items-center">
                                                                    <div className="rounded-circle me-3 d-flex align-items-center justify-content-center" style={{
                                                                        width: '40px',
                                                                        height: '40px',
                                                                        backgroundColor: '#dbeafe'
                                                                    }}>
                                                                        <i className="fas fa-user" style={{ color: '#3b82f6' }}></i>
                                                                    </div>
                                                                    <div>
                                                                        <div className="fw-semibold">{appointment.patientName}</div>
                                                                        <small className="text-muted">ID: {appointment.patient_id}</small>
                                                                    </div>
                                                                </div>
                                                            </td>
                                                            <td className="px-4 py-3">
                                                                <span className="text-muted">{appointment.note || 'Không có ghi chú'}</span>
                                                            </td>
                                                            <td className="px-4 py-3">
                                                                <span className="badge rounded-pill px-3 py-2" style={{
                                                                    backgroundColor: `${dataHelpers.getStatusColor(appointment.status)}20`,
                                                                    color: dataHelpers.getStatusColor(appointment.status),
                                                                    fontWeight: '600'
                                                                }}>
                                                                    {dataHelpers.getStatusText(appointment.status)}
                                                                </span>
                                                            </td>
                                                            <td className="px-4 py-3">
                                                                <div className="d-flex gap-2">
                                                                    <button 
                                                                        className="btn btn-sm btn-outline-primary rounded-pill"
                                                                        title="Xem chi tiết"
                                                                    >
                                                                        <i className="fas fa-eye"></i>
                                                                    </button>
                                                                    {appointment.status !== 'Completed' && (
                                                                        <button 
                                                                            className="btn btn-sm btn-outline-success rounded-pill"
                                                                            title="Hoàn thành"
                                                                            onClick={() => handleUpdateAppointmentStatus(appointment.id, 'Completed')}
                                                                        >
                                                                            <i className="fas fa-check"></i>
                                                                        </button>
                                                                    )}
                                                                </div>
                                                            </td>
                                                        </tr>
                                                    );
                                                })}
                                            </tbody>
                                        </table>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Quick Actions & Database Info */}
                    <div className="col-lg-4">
                        {/* Quick Actions */}
                        <div className="card border-0 rounded-4 mb-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-bolt me-2" style={{ color: '#f59e0b' }}></i>
                                    Thao tác nhanh
                                </h5>
                            </div>
                            <div className="card-body p-4">
                                <div className="d-grid gap-3">
                                    {[
                                        { icon: 'fa-user-plus', text: 'Thêm bệnh nhân mới', color: '#3b82f6', href: '/edit/patient' },
                                        { icon: 'fa-prescription-bottle-alt', text: 'Kê đơn thuốc', color: '#10b981', href: '#' },
                                        { icon: 'fa-file-medical', text: 'Xem hồ sơ bệnh án', color: '#6366f1', href: '/patients' },
                                        { icon: 'fa-chart-line', text: 'Báo cáo thống kê', color: '#f59e0b', href: '#' }
                                    ].map((action, index) => (
                                        <a 
                                            key={index} 
                                            href={action.href}
                                            className="btn btn-light text-start p-3 rounded-3 text-decoration-none" 
                                            style={{
                                                border: '2px solid #f1f5f9',
                                                transition: 'all 0.3s ease',
                                                color: 'inherit'
                                            }}
                                        >
                                            <i className={`fas ${action.icon} me-3`} style={{ color: action.color }}></i>
                                            <span className="fw-semibold">{action.text}</span>
                                        </a>
                                    ))}
                                </div>
                            </div>
                        </div>

                        {/* Database Info */}
                        <div className="card border-0 rounded-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-database me-2" style={{ color: '#6366f1' }}></i>
                                    Thông tin Database
                                </h5>
                            </div>
                            <div className="card-body p-4">
                                <div className="d-flex flex-column gap-3">
                                    <div className="d-flex justify-content-between align-items-center p-3 rounded-3" style={{
                                        backgroundColor: '#f8fafc',
                                        border: '1px solid #e2e8f0'
                                    }}>
                                        <div>
                                            <div className="fw-semibold">Tổng Bác sĩ</div>
                                            <small className="text-muted">Hoạt động</small>
                                        </div>
                                        <div className="h5 fw-bold mb-0" style={{ color: '#3b82f6' }}>
                                            {doctors.length}
                                        </div>
                                    </div>
                                    <div className="d-flex justify-content-between align-items-center p-3 rounded-3" style={{
                                        backgroundColor: '#f8fafc',
                                        border: '1px solid #e2e8f0'
                                    }}>
                                        <div>
                                            <div className="fw-semibold">Tổng Lịch hẹn</div>
                                            <small className="text-muted">Tất cả thời gian</small>
                                        </div>
                                        <div className="h5 fw-bold mb-0" style={{ color: '#10b981' }}>
                                            {appointments.length}
                                        </div>
                                    </div>
                                    <div className="d-flex justify-content-between align-items-center p-3 rounded-3" style={{
                                        backgroundColor: '#f8fafc',
                                        border: '1px solid #e2e8f0'
                                    }}>
                                        <div>
                                            <div className="fw-semibold">Cập nhật cuối</div>
                                            <small className="text-muted">Dữ liệu real-time</small>
                                        </div>
                                        <div className="text-success">
                                            <i className="fas fa-circle"></i> Online
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Custom Styles */}
            <style jsx>{`
                .table-hover tbody tr:hover {
                    background-color: #f8fafc !important;
                }
                
                .btn:focus {
                    box-shadow: none;
                }
                
                .card {
                    border: 1px solid rgba(0, 0, 0, 0.05);
                }
            `}</style>
        </div>
    );
};

export default DoctorDashboard;
