import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import imageCompression from 'browser-image-compression';

const EditPatient = ({ patientId = 1, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    full_name: '',
    dob: '',
    gender: '',
    phone: '',
    address: '',
    img: null,
    imgPreview: 'default.png',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`);
        if (!patientResponse.ok) {
          throw new Error('Không thể tải thông tin bệnh nhân');
        }
        const patientData = await patientResponse.json();

        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`);
        if (!accountResponse.ok) {
          throw new Error('Không thể tải thông tin tài khoản');
        }
        const accountData = await accountResponse.json();

        setFormData({
          full_name: patientData.full_name || '',
          dob: patientData.dob || '',
          gender: patientData.gender || '',
          phone: patientData.phone || '',
          address: patientData.address || '',
          img: null,
          imgPreview: accountData.img || 'default.png',
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [patientId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        const options = {
          maxSizeMB: 1,
          maxWidthOrHeight: 800,
          useWebWorker: true,
        };
        const compressedFile = await imageCompression(file, options);
        setFormData((prev) => ({
          ...prev,
          img: compressedFile,
          imgPreview: URL.createObjectURL(compressedFile),
        }));
      } catch (err) {
        setError('Không thể xử lý ảnh: ' + err.message);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: patientId,
          full_name: formData.full_name,
          dob: formData.dob,
          gender: formData.gender,
          phone: formData.phone,
          address: formData.address,
        }),
      });

      if (!patientResponse.ok) {
        throw new Error('Không thể cập nhật thông tin bệnh nhân');
      }

      if (formData.img) {
        const formDataToSend = new FormData();
        formDataToSend.append('img', formData.img);
        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`, {
          method: 'PUT',
          body: formDataToSend,
        });

        if (!accountResponse.ok) {
          throw new Error('Không thể cập nhật ảnh tài khoản');
        }
      }

      const updatedPatient = await patientResponse.json();
      onSave(updatedPatient);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center p-6 bg-red-100 text-red-700 rounded-lg shadow-md">
        <p className="font-semibold">Lỗi: {error}</p>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-8 bg-gradient-to-br from-white to-gray-50 shadow-2xl rounded-2xl my-8">
      <h2 className="text-3xl font-extrabold mb-8 text-center text-gray-800">Chỉnh sửa thông tin bệnh nhân</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="full_name" className="block text-sm font-semibold text-gray-700 mb-2">
            Họ và tên
          </label>
          <input
            type="text"
            id="full_name"
            name="full_name"
            value={formData.full_name}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 hover:border-blue-300"
            placeholder="Nhập họ và tên"
            required
          />
        </div>
        <div>
          <label htmlFor="dob" className="block text-sm font-semibold text-gray-700 mb-2">
            Ngày sinh
          </label>
          <input
            type="date"
            id="dob"
            name="dob"
            value={formData.dob}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 hover:border-blue-300"
            required
          />
        </div>
        <div>
          <label htmlFor="gender" className="block text-sm font-semibold text-gray-700 mb-2">
            Giới tính
          </label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 hover:border-blue-300"
            required
          >
            <option value="">Chọn giới tính</option>
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
          </select>
        </div>
        <div>
          <label htmlFor="phone" className="block text-sm font-semibold text-gray-700 mb-2">
            Số điện thoại
          </label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 hover:border-blue-300"
            pattern="[0-9]{10}"
            placeholder="Nhập số điện thoại (10 số)"
            required
          />
        </div>
        <div>
          <label htmlFor="address" className="block text-sm font-semibold text-gray-700 mb-2">
            Địa chỉ
          </label>
          <textarea
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 hover:border-blue-300"
            rows="4"
            placeholder="Nhập địa chỉ"
            required
          />
        </div>
        <div>
          <label htmlFor="img" className="block text-sm font-semibold text-gray-700 mb-2">
            Ảnh đại diện
          </label>
          <div className="flex items-center space-x-6">
            <div className="relative group">
              <img
                src={formData.imgPreview}
                alt="Ảnh đại diện"
                className="w-32 h-32 object-cover rounded-full border-4 border-gray-200 shadow-md group-hover:scale-105 transition-transform duration-200"
              />
              <div className="absolute inset-0 rounded-full bg-black bg-opacity-0 group-hover:bg-opacity-20 transition duration-200"></div>
            </div>
            <input
              type="file"
              id="img"
              name="img"
              accept="image/*"
              onChange={handleImageChange}
              className="file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 transition duration-200"
            />
          </div>
        </div>
        <div className="flex justify-end space-x-4 pt-4">
          <button
            type="button"
            onClick={onCancel}
            className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg shadow-md hover:bg-gray-300 transition duration-200 disabled:bg-gray-100 disabled:text-gray-400 disabled:cursor-not-allowed"
            disabled={loading}
          >
            Hủy
          </button>
          <button
            type="submit"
            onClick={handleSubmit()}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700 transition duration-200 disabled:bg-blue-300 disabled:cursor-not-allowed"
            disabled={loading}
          >
            Lưu
          </button>
        </div>
      </form>
    </div>
  );
};

EditPatient.propTypes = {
  patientId: PropTypes.string.isRequired,
  onSave: PropTypes.func.isRequired,
  onCancel: PropTypes.func.isRequired,
};

export default EditPatient;
