import { useState } from "react";
const sampleUserData = {
  AccountPatient: [
    {
      id: 1,
      username: "phamvannha",
      email: "pham.nha@gmail.com",
      status: "Enable",
    },
    // ... more patient data
  ],
  AccountStaff: [
    {
      id: 1,
      username: "doquocdat",
      email: "dat.doctor01@health.vn",
      role: "Doctor",
      status: "Enable",
    },
    // ... more staff data
  ],
};

// Component to edit user information
function EditUserForm() {
  const [userType, setUserType] = useState("patient"); // 'patient' or 'staff'
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    status: "Enable",
    role: "Doctor", // Only for staff
  });
  const [error, setError] = useState("");

  // Simulate fetching user data based on ID (e.g., from URL or prop)
  useEffect(() => {
    // Example: Fetch user with ID 1, type 'patient'
    const userId = 1; // Replace with dynamic ID from props or URL
    const type = "patient"; // Replace with dynamic type
    setUserType(type);

    let fetchedUser;
    if (type === "patient") {
      fetchedUser = sampleUserData.AccountPatient.find((u) => u.id === userId);
    } else {
      fetchedUser = sampleUserData.AccountStaff.find((u) => u.id === userId);
    }

    if (fetchedUser) {
      setUser(fetchedUser);
      setFormData({
        username: fetchedUser.username,
        email: fetchedUser.email,
        status: fetchedUser.status,
        role: fetchedUser.role || "Doctor",
      });
    } else {
      setError("User not found");
    }
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Basic validation
    if (!formData.username || !formData.email) {
      setError("Username and email are required");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      setError("Invalid email format");
      return;
    }

    // Simulate saving data
    console.log("Saving user data:", {
      ...formData,
      id: user?.id,
      type: userType,
    });
    alert("User information updated successfully!");
    setError("");
  };

  if (error) {
    return <div className="text-red-500 text-center">{error}</div>;
  }

  if (!user) {
    return <div className="text-center">Loading...</div>;
  }

  return (
    <div className="max-w-lg mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-center">
        Edit {userType === "patient" ? "Patient" : "Staff"} Information
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Username
          </label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Email
          </label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Status
          </label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
          >
            <option value="Enable">Enable</option>
            <option value="Disable">Disable</option>
          </select>
        </div>
        {userType === "staff" && (
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Role
            </label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
            >
              <option value="Doctor">Doctor</option>
              <option value="Nurse">Nurse</option>
              <option value="Receptionist">Receptionist</option>
              <option value="AdminSys">AdminSys</option>
              <option value="AdminBusiness">AdminBusiness</option>
            </select>
          </div>
        )}
        <button
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
        >
          Save Changes
        </button>
      </form>
    </div>
  );
}

export default EditUserForm;
