import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DoctorHeader from './DoctorHeader';
import { 
    getWaitlistByDoctorId, 
    getPatients, 
    getRooms,
    createExamResult,
    updateWaitlistStatus,
    createMedicineRecord,
    getExamResults,
    getExamResultsByDoctorId,
    getExamResultByMedicineRecordId,
    getMedicineRecords,
    getServiceOrdersByDoctorId,
    getServiceOrderItems,
    getResultsOfParaclinicalServices,
    getMedicalServices,
    getDiagnosisByMedicineRecordId,
    createDiagnosis,
    getMedicines,
    createPrescription,
    createPrescriptionInvoice,
    createMedicineDetail
} from '../services/api';

const DoctorDashboard = () => {
    const navigate = useNavigate();
    
    const [waitlist, setWaitlist] = useState([]);
    const [allWaitlist, setAllWaitlist] = useState([]); // For statistics
    const [patients, setPatients] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [examResults, setExamResults] = useState([]);
    const [medicineRecords, setMedicineRecords] = useState([]);
    const [serviceOrders, setServiceOrders] = useState([]);
    const [serviceOrderItems, setServiceOrderItems] = useState([]);
    const [serviceResults, setServiceResults] = useState([]);
    const [medicalServices, setMedicalServices] = useState([]);
    const [medicines, setMedicines] = useState([]);
    const [diagnosisData, setDiagnosisData] = useState([]); // Add diagnosis state
    const [loading, setLoading] = useState(true);
    const [currentDoctorId, setCurrentDoctorId] = useState('1'); // Mock doctor ID - should come from auth
    
    // Exam Modal States
    const [showExamModal, setShowExamModal] = useState(false);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [selectedWaitlistItem, setSelectedWaitlistItem] = useState(null);
    const [examForm, setExamForm] = useState({
        symptoms: '',
        preliminary_diagnosis: '',
        notes: ''
    });
    const [submitting, setSubmitting] = useState(false);

    // Diagnosis Modal States
    const [showDiagnosisModal, setShowDiagnosisModal] = useState(false);
    const [selectedServiceOrder, setSelectedServiceOrder] = useState(null);
    const [diagnosisForm, setDiagnosisForm] = useState({
        conclusion: '',
        disease: '',
        treatment_plan: ''
    });
    const [diagnosisSubmitting, setDiagnosisSubmitting] = useState(false);
    
    // Prescription States (within diagnosis modal)
    const [selectedMedicines, setSelectedMedicines] = useState([]);
    const [currentModalStep, setCurrentModalStep] = useState(1); // 1: Diagnosis, 2: Prescription

    // Fetch data on component mount
    useEffect(() => {
        fetchDashboardData();
    }, [currentDoctorId]);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            const [waitlistRes, patientsRes, roomsRes, examResultsRes, medicineRecordsRes, serviceOrdersRes, serviceOrderItemsRes, serviceResultsRes, medicalServicesRes, medicinesRes] = await Promise.all([
                getWaitlistByDoctorId(currentDoctorId),
                getPatients(),
                getRooms(),
                getExamResultsByDoctorId(currentDoctorId),
                getMedicineRecords(),
                getServiceOrdersByDoctorId(currentDoctorId),
                getServiceOrderItems(),
                getResultsOfParaclinicalServices(),
                getMedicalServices(),
                getMedicines()
            ]);

            if (waitlistRes.success) {
                setAllWaitlist(waitlistRes.data);
                // Filter for different states according to new workflow
                setWaitlist(waitlistRes.data);
            }

            if (patientsRes.success) {
                setPatients(patientsRes.data);
            }

            if (roomsRes.success) {
                setRooms(roomsRes.data);
            }

            if (examResultsRes.success) {
                setExamResults(examResultsRes.data);
            }

            if (medicineRecordsRes.success) {
                setMedicineRecords(medicineRecordsRes.data);
            }

            if (serviceOrdersRes.success) {
                setServiceOrders(serviceOrdersRes.data);
            }

            if (serviceOrderItemsRes.success) {
                setServiceOrderItems(serviceOrderItemsRes.data);
            }

            if (serviceResultsRes.success) {
                setServiceResults(serviceResultsRes.data);
            }

            if (medicalServicesRes.success) {
                setMedicalServices(medicalServicesRes.data);
            }

            if (medicinesRes.success) {
                setMedicines(medicinesRes.data);
            }

            // Load diagnosis data for each service order
            if (serviceOrdersRes.success) {
                const diagnosisPromises = serviceOrdersRes.data.map(async (order) => {
                    const diagnosisRes = await getDiagnosisByMedicineRecordId(order.medicineRecord_id);
                    return {
                        serviceOrderId: order.id,
                        medicineRecordId: order.medicineRecord_id,
                        diagnosis: diagnosisRes.success ? diagnosisRes.data : []
                    };
                });

                const diagnosisResults = await Promise.all(diagnosisPromises);
                setDiagnosisData(diagnosisResults);
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            alert('❌ Có lỗi xảy ra khi tải dữ liệu!');
        } finally {
            setLoading(false);
        }
    };

    // Get patient info by ID
    const getPatientInfo = (patientId) => {
        return patients.find(p => p.id === patientId) || {};
    };

    // Get room info by ID
    const getRoomInfo = (roomId) => {
        return rooms.find(r => r.id === roomId) || {};
    };

    // Format time display
    const formatTime = (timeString) => {
        return new Date(timeString).toLocaleTimeString('vi-VN', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    // FILTER FUNCTIONS FOR NEW WORKFLOW
    
    // 1. Waiting for Examination: status=Waiting + visitType=Initial
    const getWaitingForExamination = () => {
        return allWaitlist.filter(item => 
            item.status === 'Waiting' && item.visittype === 'Initial'
        );
    };

    // 2. Ready for Service Assignment: status=InProgress + visitType=Initial  
    const getReadyForServiceAssignment = () => {
        return allWaitlist.filter(item => 
            item.status === 'InProgress' && item.visittype === 'Initial'
        );
    };

    // 3. Waiting for Service Results: status=Waiting + visitType=Result
    const getWaitingForServiceResults = () => {
        return allWaitlist.filter(item => 
            item.status === 'Waiting' && item.visittype === 'Result'
        );
    };

    // 4. Ready for Diagnosis: status=Waiting + visitType=Result + có kết quả dịch vụ đầy đủ + chưa có diagnosis
    const getReadyForDiagnosis = () => {
        console.log('\n🔍 DEBUG getReadyForDiagnosis - Bước 4:');
        
        const filteredResults = allWaitlist.filter(item => {
            console.log(`\n--- Kiểm tra bệnh nhân ${item.patient_id} ---`);
            console.log('Status:', item.status, 'VisitType:', item.visittype);
            
            if (item.status !== 'Waiting' || item.visittype !== 'Result') {
                console.log('❌ Không đúng status/visittype');
                return false;
            }
            
            // Check if has service order - get LATEST medicine record for this patient
            const patientRecords = medicineRecords.filter(record => record.patient_id === item.patient_id);
            console.log('Medicine records found:', patientRecords.length);
            
            if (patientRecords.length === 0) {
                console.log('❌ Không có medicine record');
                return false;
            }
            
            // Get the latest medicine record (highest ID)
            const latestMedicineRecord = patientRecords.sort((a, b) => b.id - a.id)[0];
            console.log('Latest medicine record:', latestMedicineRecord.id);
            
            const serviceOrder = serviceOrders.find(order => order.medicineRecord_id === latestMedicineRecord.id);
            console.log('Service order found:', serviceOrder?.id);
            
            if (!serviceOrder) {
                console.log('❌ Không có service order');
                return false;
            }
            
            // Kiểm tra service order đã có kết quả ResultsOfParaclinicalServices chưa
            const isCompleted = isServiceOrderCompleted(serviceOrder);
            const hasDiagnosis = hasServiceOrderDiagnosis(serviceOrder.id);
            
            console.log('Service completed:', isCompleted);
            console.log('Has diagnosis:', hasDiagnosis);
            
            const result = isCompleted && !hasDiagnosis;
            console.log('Final result for step 4:', result);
            
            // Chỉ hiển thị khi: có kết quả dịch vụ đầy đủ && chưa có kết luận điều trị
            return result;
        });
        
        console.log('🎯 Tổng số bệnh nhân ở bước 4:', filteredResults.length);
        return filteredResults;
    };

    // 5. Completed Treatment: status=Complete + visitType=Result  
    const getCompletedTreatment = () => {
        return allWaitlist.filter(item => 
            item.status === 'Complete' && item.visittype === 'Result'
        );
    };

    // Handle start examination
    const handleStartExam = (waitlistItem, actionType) => {
        const patient = getPatientInfo(waitlistItem.patient_id);
        
        if (actionType === 'assign') {
            // Navigate to service assignment page
            navigate(`/service-assignment/${waitlistItem.patient_id}/${waitlistItem.id}`);
        } else if (actionType === 'view') {
            // Navigate to result page to view results
            navigate(`/results/${waitlistItem.patient_id}`);
        } else {
            // Open exam modal for examination
            setSelectedWaitlistItem(waitlistItem);
            setSelectedPatient(patient);
            setShowExamModal(true);
            setExamForm({
                symptoms: '',
                preliminary_diagnosis: '',
                notes: ''
            });
        }
    };

    // Handle exam form submission
    const handleExamSubmit = async (e) => {
        e.preventDefault();
        
        if (!examForm.symptoms.trim() || !examForm.preliminary_diagnosis.trim()) {
            alert('Vui lòng nhập đầy đủ triệu chứng và chẩn đoán sơ bộ!');
            return;
        }

        setSubmitting(true);

        try {
            // Always create NEW medicine record for each examination visit
            const newRecordRes = await createMedicineRecord({
                patient_id: selectedPatient.id
            });
            
            let medicineRecordId;
            if (newRecordRes.success) {
                medicineRecordId = newRecordRes.data.id;
            } else {
                throw new Error('Không thể tạo hồ sơ bệnh án mới cho lần khám này');
            }

            // Create exam result
            const examData = {
                medicineRecord_id: medicineRecordId,
                symptoms: examForm.symptoms,
                preliminary_diagnosis: examForm.preliminary_diagnosis,
                doctor_id: currentDoctorId,
                notes: examForm.notes || ''
            };

            const examResultRes = await createExamResult(examData);
            
            if (examResultRes.success) {
                // Update status=InProgress, visitType remains Initial (ready for service assignment)
                const updateRes = await updateWaitlistStatus(selectedWaitlistItem.id, 'InProgress');
                
                if (updateRes.success) {
                    alert('✅ Đã hoàn thành khám lâm sàng! Có thể chỉ định dịch vụ.');
                    setShowExamModal(false);
                    fetchDashboardData(); 
                } else {
                    alert('Đã lưu kết quả khám nhưng không thể cập nhật trạng thái!');
                }
            } else {
                throw new Error('Không thể lưu kết quả khám');
            }

        } catch (error) {
            console.error('Error saving exam result:', error);
            alert('❌ Có lỗi xảy ra khi lưu kết quả khám: ' + error.message);
        } finally {
            setSubmitting(false);
        }
    };

    // Get visit type text in Vietnamese
    const getVisitTypeText = (visitType) => {
        switch(visitType) {
            case 'Initial': return 'Khám lần đầu';
            case 'Result': return 'Xem kết quả';
            default: return visitType;
        }
    };

    // Get status badge for waitlist
    const getStatusBadge = (status) => {
        switch(status) {
            case 'Waiting': 
                return <span className="badge bg-info">Chờ khám</span>;
            case 'InProgress': 
                return <span className="badge bg-warning">Đang điều trị</span>;
            case 'Complete': 
                return <span className="badge bg-success">Hoàn thành</span>;
            default: 
                return <span className="badge bg-secondary">{status}</span>;
        }
    };

    // Get service order items for a specific order
    const getServiceOrderItemsForOrder = (serviceOrderId) => {
        return serviceOrderItems.filter(item => item.service_order_id === serviceOrderId);
    };

    // Get service results for a specific order
    const getServiceResultsForOrder = (serviceOrderId) => {
        const orderItems = getServiceOrderItemsForOrder(serviceOrderId);
        const orderItemIds = orderItems.map(item => item.id);
        return serviceResults.filter(result => orderItemIds.includes(result.service_order_item_id));
    };

    // Check if service order is fully completed (all services have results)
    const isServiceOrderCompleted = (serviceOrder) => {
        // Lấy tất cả ServiceOrderItem cho service order này
        const orderItems = getServiceOrderItemsForOrder(serviceOrder.id);
        
        if (orderItems.length === 0) return false;
        
        // Kiểm tra từng ServiceOrderItem đã có kết quả trong ResultsOfParaclinicalServices chưa
        for (const orderItem of orderItems) {
            const hasResult = serviceResults.some(result => 
                result.service_order_item_id === orderItem.id && 
                result.result_description && 
                result.result_description.trim() !== ''
            );
            
            if (!hasResult) {
                console.log(`❌ ServiceOrderItem ${orderItem.id} chưa có kết quả`);
                return false;
            }
        }
        
        console.log(`✅ ServiceOrder ${serviceOrder.id} đã có đầy đủ kết quả (${orderItems.length} items)`);
        return true;
    };

    // Check if service order has diagnosis
    const hasServiceOrderDiagnosis = (serviceOrderId) => {
        const diagnosisEntry = diagnosisData.find(entry => entry.serviceOrderId === serviceOrderId);
        return diagnosisEntry && diagnosisEntry.diagnosis.length > 0;
    };

    // Get service info by ID
    const getServiceInfo = (serviceId) => {
        return medicalServices.find(s => s.id === serviceId) || {};
    };

    // Handle diagnosis modal
    const handleOpenDiagnosisModal = (waitlistItem) => {
        // Find latest service order for this patient
        const patientRecords = medicineRecords.filter(record => record.patient_id === waitlistItem.patient_id);
        if (patientRecords.length === 0) return;
        
        const latestMedicineRecord = patientRecords.sort((a, b) => b.id - a.id)[0];
        
        const serviceOrder = serviceOrders.find(order => order.medicineRecord_id === latestMedicineRecord.id);
        if (!serviceOrder) return;
        
        setSelectedServiceOrder(serviceOrder);
        setSelectedWaitlistItem(waitlistItem);
        setDiagnosisForm({
            conclusion: '',
            disease: '',
            treatment_plan: ''
        });
        setSelectedMedicines([]);
        setCurrentModalStep(1);
        setShowDiagnosisModal(true);
    };

    // Handle medicine selection
    const handleMedicineToggle = (medicineId) => {
        setSelectedMedicines(prev => {
            const existingIndex = prev.findIndex(item => item.medicineId === medicineId);
            
            if (existingIndex >= 0) {
                return prev.filter(item => item.medicineId !== medicineId);
            } else {
                return [...prev, { 
                    medicineId, 
                    quantity: 1, 
                    dosage: '1 viên x 3 lần/ngày' 
                }];
            }
        });
    };

    const handleMedicineChange = (medicineId, field, value) => {
        setSelectedMedicines(prev => 
            prev.map(item => 
                item.medicineId === medicineId 
                    ? { ...item, [field]: value }
                    : item
            )
        );
    };

    const getMedicinePrice = (medicine) => {
        return medicine.price && !isNaN(medicine.price) 
            ? new Intl.NumberFormat('vi-VN').format(medicine.price) + ' VNĐ'
            : 'Liên hệ';
    };

    // Handle diagnosis submission (step 1)
    const handleDiagnosisSubmit = async (e) => {
        e.preventDefault();
        
        if (!diagnosisForm.conclusion.trim() || !diagnosisForm.disease.trim() || !diagnosisForm.treatment_plan.trim()) {
            alert('Vui lòng nhập đầy đủ thông tin kết luận điều trị!');
            return;
        }

        setDiagnosisSubmitting(true);

        try {
            const diagnosisData = {
                doctor_id: currentDoctorId,
                medicineRecord_id: selectedServiceOrder.medicineRecord_id,
                conclusion: diagnosisForm.conclusion,
                disease: diagnosisForm.disease,
                treatment_plan: diagnosisForm.treatment_plan
            };

            const result = await createDiagnosis(diagnosisData);
            if (result.success) {
                // Refresh diagnosis data
                await fetchDashboardData();
                // Move to prescription step
                setCurrentModalStep(2);
                alert('✅ Lưu kết luận điều trị thành công! Tiến hành kê đơn thuốc.');
            } else {
                throw new Error('Không thể lưu kết luận điều trị');
            }

        } catch (error) {
            console.error('Error creating diagnosis:', error);
            alert('❌ Có lỗi xảy ra khi lưu kết luận điều trị!');
        } finally {
            setDiagnosisSubmitting(false);
        }
    };

    // Handle prescription submission (step 2)
    const handlePrescriptionSubmit = async () => {
        if (selectedMedicines.length === 0) {
            alert('Vui lòng chọn ít nhất một loại thuốc hoặc bỏ qua để hoàn thành!');
            return;
        }

        const invalidMedicines = selectedMedicines.filter(item => !item.quantity || !item.dosage);
        if (invalidMedicines.length > 0) {
            alert('Vui lòng nhập đầy đủ số lượng và liều dùng cho tất cả thuốc!');
            return;
        }

        setDiagnosisSubmitting(true);

        try {
            // Create prescription
            const prescriptionData = {
                medicineRecord_id: selectedServiceOrder.medicineRecord_id,
                doctor_id: currentDoctorId,
                prescription_date: new Date().toISOString().split('T')[0],
                status: 'Pending'
            };

            const prescriptionRes = await createPrescription(prescriptionData);
            if (!prescriptionRes.success) {
                throw new Error('Không thể tạo đơn thuốc');
            }

            const prescriptionId = prescriptionRes.data.id;

            // Create prescription invoice
            const invoiceData = {
                invoice_id: `INV${Date.now()}`,
                pharmacist_id: '1',
                prescription_id: prescriptionId
            };

            const invoiceRes = await createPrescriptionInvoice(invoiceData);
            if (!invoiceRes.success) {
                throw new Error('Không thể tạo hóa đơn thuốc');
            }

            const prescriptionInvoiceId = invoiceRes.data.id;

            // Create medicine details
            for (const medicine of selectedMedicines) {
                const medicineDetailData = {
                    prescription_invoice_id: prescriptionInvoiceId,
                    medicine_id: medicine.medicineId,
                    quantity: medicine.quantity.toString(),
                    dosage: medicine.dosage
                };

                const detailRes = await createMedicineDetail(medicineDetailData);
                if (!detailRes.success) {
                    console.error('Failed to create medicine detail:', medicine);
                }
            }

            // Final step: Update to Complete status, visitType remains Result
            await updateWaitlistStatus(selectedWaitlistItem.id, 'Complete');

            alert('✅ Kê đơn thuốc thành công! Hoàn thành toàn bộ quy trình điều trị.');
            setShowDiagnosisModal(false);
            await fetchDashboardData();

        } catch (error) {
            console.error('Error creating prescription:', error);
            alert('❌ Có lỗi xảy ra khi kê đơn thuốc: ' + error.message);
        } finally {
            setDiagnosisSubmitting(false);
        }
    };

    // Skip prescription step
    const handleSkipPrescription = async () => {
        try {
            // Final step: Update to Complete status, visitType remains Result
            await updateWaitlistStatus(selectedWaitlistItem.id, 'Complete');
            
            alert('✅ Hoàn thành kết luận điều trị (không kê đơn thuốc).');
            setShowDiagnosisModal(false);
            await fetchDashboardData();
        } catch (error) {
            console.error('Error updating waitlist status:', error);
            alert('✅ Hoàn thành kết luận điều trị (không kê đơn thuốc).');
            setShowDiagnosisModal(false);
            await fetchDashboardData();
        }
    };

    if (loading) {
        return (
            <div className="container-fluid py-5">
                <div className="text-center">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Đang tải...</span>
                    </div>
                    <p className="mt-3 text-muted">Đang tải dữ liệu...</p>
                </div>
            </div>
        );
    }

    return (
        <>
            <DoctorHeader />
            <div className="container-fluid py-4">
                {/* Dashboard Header */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                    <div>
                        <h2 className="fw-bold text-dark mb-1">
                            <i className="fas fa-chart-line text-primary me-2"></i>
                            Dashboard Bác Sĩ
                        </h2>
                        <p className="text-muted mb-0">Quản lý quy trình khám chữa bệnh</p>
                    </div>
                    <button 
                        className="btn btn-outline-primary"
                        onClick={fetchDashboardData}
                        disabled={loading}
                    >
                        <i className="fas fa-sync-alt me-2"></i>
                        Làm mới
                    </button>
                </div>

                {/* Statistics Cards */}
                <div className="row mb-4">
                    <div className="col-md-3">
                        <div className="card border-0 shadow-sm">
                            <div className="card-body text-center">
                                <div className="text-info fs-1 mb-2">
                                    <i className="fas fa-clock"></i>
                                </div>
                                <h3 className="fw-bold text-info">{getWaitingForExamination().length}</h3>
                                <p className="text-muted mb-0">Chờ khám</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card border-0 shadow-sm">
                            <div className="card-body text-center">
                                <div className="text-warning fs-1 mb-2">
                                    <i className="fas fa-stethoscope"></i>
                                </div>
                                <h3 className="fw-bold text-warning">
                                    {getReadyForServiceAssignment().length + getWaitingForServiceResults().length + getReadyForDiagnosis().length}
                                </h3>
                                <p className="text-muted mb-0">Đang điều trị</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card border-0 shadow-sm">
                            <div className="card-body text-center">
                                <div className="text-success fs-1 mb-2">
                                    <i className="fas fa-check-double"></i>
                                </div>
                                <h3 className="fw-bold text-success">{getCompletedTreatment().length}</h3>
                                <p className="text-muted mb-0">Đã hoàn thành</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card border-0 shadow-sm">
                            <div className="card-body text-center">
                                <div className="text-primary fs-1 mb-2">
                                    <i className="fas fa-users"></i>
                                </div>
                                <h3 className="fw-bold text-primary">{allWaitlist.length}</h3>
                                <p className="text-muted mb-0">Tổng số</p>
                            </div>
                        </div>
                    </div>
                </div>

                {/* 1. Waiting for Examination Section */}
                <div className="card border-0 shadow-sm mb-4">
                    <div className="card-header bg-info text-white">
                        <div className="d-flex justify-content-between align-items-center">
                            <h5 className="mb-0">
                                <i className="fas fa-clipboard-list me-2"></i>
                                1. Bệnh Nhân Chờ Khám Lâm Sàng
                            </h5>
                            <span className="badge bg-light text-dark fs-6">
                                {getWaitingForExamination().length} bệnh nhân
                            </span>
                        </div>
                    </div>
                    <div className="card-body p-0">
                        {getWaitingForExamination().length === 0 ? (
                            <div className="text-center py-5">
                                <i className="fas fa-clipboard-list fs-1 text-muted mb-3"></i>
                                <h5 className="text-muted">Không có bệnh nhân nào chờ khám</h5>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>STT</th>
                                            <th>Bệnh nhân</th>
                                            <th>Phòng</th>
                                            <th>Thời gian</th>
                                            <th>Trạng thái</th>
                                            <th>Thao tác</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getWaitingForExamination().map((item, index) => {
                                            const patient = getPatientInfo(item.patient_id);
                                            const room = getRoomInfo(item.room_id);
                                            
                                            return (
                                                <tr key={item.id}>
                                                    <td className="fw-bold text-primary">{index + 1}</td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{patient.full_name}</div>
                                                            <small className="text-muted">{patient.phone}</small>
                                                        </div>
                                                    </td>
                                                    <td>{room.room_name}</td>
                                                    <td>{formatTime(item.estimated_time)}</td>
                                                    <td>
                                                        <span className="badge bg-info">Chờ khám</span>
                                                    </td>
                                                    <td>
                                                        <button
                                                            className="btn btn-primary btn-sm"
                                                            onClick={() => handleStartExam(item, 'exam')}
                                                        >
                                                            <i className="fas fa-stethoscope me-1"></i>
                                                            Khám
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>

                {/* 2. Ready for Service Assignment Section */}
                <div className="card border-0 shadow-sm mb-4">
                    <div className="card-header bg-warning text-dark">
                        <div className="d-flex justify-content-between align-items-center">
                            <h5 className="mb-0">
                                <i className="fas fa-clipboard-check me-2"></i>
                                2. Đã Khám - Cần Chỉ Định Dịch Vụ
                            </h5>
                            <span className="badge bg-dark fs-6">
                                {getReadyForServiceAssignment().length} bệnh nhân
                            </span>
                        </div>
                    </div>
                    <div className="card-body p-0">
                        {getReadyForServiceAssignment().length === 0 ? (
                            <div className="text-center py-5">
                                <i className="fas fa-clipboard-check fs-1 text-muted mb-3"></i>
                                <h5 className="text-muted">Không có bệnh nhân nào cần chỉ định dịch vụ</h5>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>STT</th>
                                            <th>Bệnh nhân</th>
                                            <th>Chẩn đoán sơ bộ</th>
                                            <th>Trạng thái</th>
                                            <th>Thao tác</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getReadyForServiceAssignment().map((item, index) => {
                                            const patient = getPatientInfo(item.patient_id);
                                            const patientRecords = medicineRecords.filter(record => record.patient_id === item.patient_id);
                                            const latestMedicineRecord = patientRecords.length > 0 ? patientRecords.sort((a, b) => b.id - a.id)[0] : null;
                                            const examResult = latestMedicineRecord ? examResults.find(result => result.medicineRecord_id === latestMedicineRecord.id) : null;
                                            
                                            return (
                                                <tr key={item.id}>
                                                    <td className="fw-bold text-primary">{index + 1}</td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{patient.full_name}</div>
                                                            <small className="text-muted">{patient.phone}</small>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <span className="text-wrap" style={{maxWidth: '200px', display: 'inline-block'}}>
                                                            {examResult?.preliminary_diagnosis || 'N/A'}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-warning">Cần chỉ định</span>
                                                    </td>
                                                    <td>
                                                        <button
                                                            className="btn btn-warning btn-sm"
                                                            onClick={() => handleStartExam(item, 'assign')}
                                                        >
                                                            <i className="fas fa-clipboard-list me-1"></i>
                                                            Chỉ định dịch vụ
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>

                {/* 3. Waiting for Service Results Section */}
                <div className="card border-0 shadow-sm mb-4">
                    <div className="card-header bg-secondary text-white">
                        <div className="d-flex justify-content-between align-items-center">
                            <h5 className="mb-0">
                                <i className="fas fa-flask me-2"></i>
                                3. Đang Thực Hiện Dịch Vụ
                            </h5>
                            <span className="badge bg-light text-dark fs-6">
                                {getWaitingForServiceResults().length} bệnh nhân
                            </span>
                        </div>
                    </div>
                    <div className="card-body p-0">
                        {getWaitingForServiceResults().length === 0 ? (
                            <div className="text-center py-5">
                                <i className="fas fa-flask fs-1 text-muted mb-3"></i>
                                <h5 className="text-muted">Không có bệnh nhân nào đang làm dịch vụ</h5>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>STT</th>
                                            <th>Bệnh nhân</th>
                                            <th>Dịch vụ đã chỉ định</th>
                                            <th>Trạng thái</th>
                                            <th>Thao tác</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getWaitingForServiceResults().map((item, index) => {
                                            const patient = getPatientInfo(item.patient_id);
                                            const patientRecords = medicineRecords.filter(record => record.patient_id === item.patient_id);
                                            const latestMedicineRecord = patientRecords.length > 0 ? patientRecords.sort((a, b) => b.id - a.id)[0] : null;
                                            const serviceOrder = latestMedicineRecord ? serviceOrders.find(order => order.medicineRecord_id === latestMedicineRecord.id) : null;
                                            const orderItems = serviceOrder ? getServiceOrderItemsForOrder(serviceOrder.id) : [];
                                            const results = serviceOrder ? getServiceResultsForOrder(serviceOrder.id) : [];
                                            
                                            return (
                                                <tr key={item.id}>
                                                    <td className="fw-bold text-primary">{index + 1}</td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{patient.full_name}</div>
                                                            <small className="text-muted">{patient.phone}</small>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <div className="text-wrap" style={{maxWidth: '250px'}}>
                                                            {orderItems.map((orderItem, idx) => {
                                                                const service = getServiceInfo(orderItem.service_id);
                                                                return (
                                                                    <div key={orderItem.id} className="mb-1">
                                                                        <span className="badge bg-info me-1">{idx + 1}</span>
                                                                        {service.name || 'Dịch vụ không xác định'}
                                                                    </div>
                                                                );
                                                            })}
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-secondary">
                                                            Đang thực hiện ({results.length}/{orderItems.length})
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <button
                                                            className="btn btn-outline-secondary btn-sm"
                                                            onClick={() => handleStartExam(item, 'view')}
                                                        >
                                                            <i className="fas fa-eye me-1"></i>
                                                            Xem
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>

                {/* 4. Ready for Diagnosis Section */}
                <div className="card border-0 shadow-sm mb-4">
                    <div className="card-header bg-danger text-white">
                        <div className="d-flex justify-content-between align-items-center">
                            <h5 className="mb-0">
                                <i className="fas fa-file-medical me-2"></i>
                                4. Có Kết Quả - Cần Kết Luận Điều Trị
                            </h5>
                            <span className="badge bg-light text-dark fs-6">
                                {getReadyForDiagnosis().length} bệnh nhân
                            </span>
                        </div>
                    </div>
                    <div className="card-body p-0">
                        {getReadyForDiagnosis().length === 0 ? (
                            <div className="text-center py-5">
                                <i className="fas fa-file-medical fs-1 text-muted mb-3"></i>
                                <h5 className="text-muted">Không có bệnh nhân nào cần kết luận điều trị</h5>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>STT</th>
                                            <th>Bệnh nhân</th>
                                            <th>Dịch vụ đã hoàn thành</th>
                                            <th>Trạng thái</th>
                                            <th>Thao tác</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getReadyForDiagnosis().map((item, index) => {
                                            const patient = getPatientInfo(item.patient_id);
                                            const patientRecords = medicineRecords.filter(record => record.patient_id === item.patient_id);
                                            const latestMedicineRecord = patientRecords.length > 0 ? patientRecords.sort((a, b) => b.id - a.id)[0] : null;
                                            const serviceOrder = latestMedicineRecord ? serviceOrders.find(order => order.medicineRecord_id === latestMedicineRecord.id) : null;
                                            const orderItems = serviceOrder ? getServiceOrderItemsForOrder(serviceOrder.id) : [];
                                            const results = serviceOrder ? getServiceResultsForOrder(serviceOrder.id) : [];
                                            
                                            return (
                                                <tr key={item.id}>
                                                    <td className="fw-bold text-primary">{index + 1}</td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{patient.full_name}</div>
                                                            <small className="text-muted">{patient.phone}</small>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <div className="text-wrap" style={{maxWidth: '250px'}}>
                                                            {orderItems.map((orderItem, idx) => {
                                                                const service = getServiceInfo(orderItem.service_id);
                                                                return (
                                                                    <div key={orderItem.id} className="mb-1">
                                                                        <span className="badge bg-success me-1">{idx + 1}</span>
                                                                        {service.name || 'Dịch vụ không xác định'}
                                                                    </div>
                                                                );
                                                            })}
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-success">
                                                            <i className="fas fa-check-circle me-1"></i>
                                                            Hoàn thành ({results.length}/{orderItems.length})
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <button
                                                            className="btn btn-danger btn-sm"
                                                            onClick={() => handleOpenDiagnosisModal(item)}
                                                        >
                                                            <i className="fas fa-file-medical me-1"></i>
                                                            Kết luận điều trị
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>

                {/* 5. Completed Treatment Section */}
                <div className="card border-0 shadow-sm mb-4">
                    <div className="card-header bg-success text-white">
                        <div className="d-flex justify-content-between align-items-center">
                            <h5 className="mb-0">
                                <i className="fas fa-check-circle me-2"></i>
                                5. Đã Hoàn Thành Điều Trị
                            </h5>
                            <span className="badge bg-light text-dark fs-6">
                                {getCompletedTreatment().length} bệnh nhân
                            </span>
                        </div>
                    </div>
                    <div className="card-body p-0">
                        {getCompletedTreatment().length === 0 ? (
                            <div className="text-center py-5">
                                <i className="fas fa-check-circle fs-1 text-muted mb-3"></i>
                                <h5 className="text-muted">Chưa có bệnh nhân nào hoàn thành điều trị</h5>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>STT</th>
                                            <th>Bệnh nhân</th>
                                            <th>Chẩn đoán cuối</th>
                                            <th>Ngày hoàn thành</th>
                                            <th>Trạng thái</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getCompletedTreatment().map((item, index) => {
                                            const patient = getPatientInfo(item.patient_id);
                                            const patientRecords = medicineRecords.filter(record => record.patient_id === item.patient_id);
                                            const latestMedicineRecord = patientRecords.length > 0 ? patientRecords.sort((a, b) => b.id - a.id)[0] : null;
                                            const diagnosisEntry = diagnosisData.find(entry => entry.medicineRecordId === latestMedicineRecord?.id);
                                            const diagnosis = diagnosisEntry ? diagnosisEntry.diagnosis[0] : null;
                                            
                                            return (
                                                <tr key={item.id}>
                                                    <td className="fw-bold text-primary">{index + 1}</td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{patient.full_name}</div>
                                                            <small className="text-muted">{patient.phone}</small>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        {diagnosis ? (
                                                            <div className="text-wrap" style={{maxWidth: '200px'}}>
                                                                <strong className="text-danger">{diagnosis.disease}</strong>
                                                                <br />
                                                                <small className="text-muted">{diagnosis.conclusion}</small>
                                                            </div>
                                                        ) : (
                                                            <span className="text-muted">Không có thông tin</span>
                                                        )}
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-secondary">
                                                            {new Date(item.updated_at || item.registered_at).toLocaleDateString('vi-VN')}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-success">
                                                            <i className="fas fa-check-double me-1"></i>
                                                            Hoàn thành
                                                        </span>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>

                {/* Exam Modal */}
                {showExamModal && (
                    <div className="modal fade show d-block" tabIndex="-1" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
                        <div className="modal-dialog modal-lg">
                            <div className="modal-content">
                                <div className="modal-header bg-primary text-white">
                                    <h5 className="modal-title">
                                        <i className="fas fa-stethoscope me-2"></i>
                                        Khám Bệnh Lâm Sàng
                                    </h5>
                                    <button 
                                        type="button" 
                                        className="btn-close btn-close-white" 
                                        onClick={() => setShowExamModal(false)}
                                        disabled={submitting}
                                    ></button>
                                </div>
                                <div className="modal-body">
                                    {/* Patient Info */}
                                    <div className="alert alert-info">
                                        <h6 className="mb-2">
                                            <i className="fas fa-user me-2"></i>
                                            Thông tin bệnh nhân
                                        </h6>
                                        <div className="row">
                                            <div className="col-md-6">
                                                <strong>Họ tên:</strong> {selectedPatient?.full_name}
                                            </div>
                                            <div className="col-md-6">
                                                <strong>Giới tính:</strong> {selectedPatient?.gender}
                                            </div>
                                            <div className="col-md-6">
                                                <strong>Ngày sinh:</strong> {selectedPatient?.dob}
                                            </div>
                                            <div className="col-md-6">
                                                <strong>Điện thoại:</strong> {selectedPatient?.phone}
                                            </div>
                                        </div>
                                    </div>

                                    {/* Exam Form */}
                                    <form onSubmit={handleExamSubmit}>
                                        <div className="row">
                                            <div className="col-12 mb-3">
                                                <label className="form-label fw-semibold">
                                                    <i className="fas fa-thermometer-half me-2 text-danger"></i>
                                                    Triệu chứng <span className="text-danger">*</span>
                                                </label>
                                                <textarea
                                                    className="form-control"
                                                    rows="4"
                                                    placeholder="Mô tả các triệu chứng của bệnh nhân..."
                                                    value={examForm.symptoms}
                                                    onChange={(e) => setExamForm(prev => ({...prev, symptoms: e.target.value}))}
                                                    required
                                                    disabled={submitting}
                                                />
                                            </div>

                                            <div className="col-12 mb-3">
                                                <label className="form-label fw-semibold">
                                                    <i className="fas fa-diagnoses me-2 text-primary"></i>
                                                    Chẩn đoán sơ bộ <span className="text-danger">*</span>
                                                </label>
                                                <textarea
                                                    className="form-control"
                                                    rows="3"
                                                    placeholder="Nhập chẩn đoán sơ bộ..."
                                                    value={examForm.preliminary_diagnosis}
                                                    onChange={(e) => setExamForm(prev => ({...prev, preliminary_diagnosis: e.target.value}))}
                                                    required
                                                    disabled={submitting}
                                                />
                                            </div>

                                            <div className="col-12 mb-3">
                                                <label className="form-label fw-semibold">
                                                    <i className="fas fa-sticky-note me-2 text-warning"></i>
                                                    Ghi chú thêm
                                                </label>
                                                <textarea
                                                    className="form-control"
                                                    rows="2"
                                                    placeholder="Ghi chú thêm (tùy chọn)..."
                                                    value={examForm.notes}
                                                    onChange={(e) => setExamForm(prev => ({...prev, notes: e.target.value}))}
                                                    disabled={submitting}
                                                />
                                            </div>
                                        </div>
                                    </form>
                                </div>
                                <div className="modal-footer">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary" 
                                        onClick={() => setShowExamModal(false)}
                                        disabled={submitting}
                                    >
                                        <i className="fas fa-times me-2"></i>
                                        Hủy
                                    </button>
                                    <button 
                                        type="submit" 
                                        className="btn btn-primary"
                                        onClick={handleExamSubmit}
                                        disabled={submitting}
                                    >
                                        {submitting ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2"></span>
                                                Đang lưu...
                                            </>
                                        ) : (
                                            <>
                                                <i className="fas fa-save me-2"></i>
                                                Lưu kết quả khám
                                            </>
                                        )}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {/* Diagnosis Modal */}
                {showDiagnosisModal && selectedServiceOrder && (
                    <div className="modal fade show d-block" tabIndex="-1" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
                        <div className="modal-dialog modal-xl">
                            <div className="modal-content">
                                <div className="modal-header bg-warning text-dark">
                                    <h5 className="modal-title">
                                        <i className="fas fa-file-medical me-2"></i>
                                        {currentModalStep === 1 ? 'Kết Luận Điều Trị' : 'Kê Đơn Thuốc'}
                                    </h5>
                                    <div className="d-flex align-items-center">
                                        <span className={`badge me-2 ${currentModalStep === 1 ? 'bg-primary' : 'bg-secondary'}`}>
                                            1. Kết luận
                                        </span>
                                        <span className={`badge ${currentModalStep === 2 ? 'bg-primary' : 'bg-secondary'}`}>
                                            2. Kê đơn thuốc
                                        </span>
                                    </div>
                                    <button 
                                        type="button" 
                                        className="btn-close" 
                                        onClick={() => setShowDiagnosisModal(false)}
                                        disabled={diagnosisSubmitting}
                                    ></button>
                                </div>
                                <div className="modal-body">
                                    {/* Patient Info - Always visible */}
                                    {(() => {
                                        const medicineRecord = medicineRecords.find(record => record.id === selectedServiceOrder.medicineRecord_id);
                                        const patient = medicineRecord ? getPatientInfo(medicineRecord.patient_id) : null;
                                        
                                        return (
                                            <div className="row mb-4">
                                                <div className="col-12">
                                                    <div className="card bg-light">
                                                        <div className="card-body">
                                                            <h6 className="fw-bold text-primary mb-3">
                                                                <i className="fas fa-user me-2"></i>
                                                                Thông tin bệnh nhân
                                                            </h6>
                                                            <div className="row">
                                                                <div className="col-md-4">
                                                                    <p><strong>Họ tên:</strong> {patient?.full_name || 'N/A'}</p>
                                                                    <p><strong>Điện thoại:</strong> {patient?.phone || 'N/A'}</p>
                                                                </div>
                                                                <div className="col-md-4">
                                                                    <p><strong>Ngày sinh:</strong> {patient?.dob || 'N/A'}</p>
                                                                    <p><strong>Giới tính:</strong> {patient?.gender || 'N/A'}</p>
                                                                </div>
                                                                <div className="col-md-4">
                                                                    <p><strong>Ngày chỉ định:</strong> {new Date(selectedServiceOrder.order_date).toLocaleDateString('vi-VN')}</p>
                                                                    <p><strong>Mã đơn:</strong> SO-{selectedServiceOrder.id}</p>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })()}

                                    {/* Step 1: Service Results and Diagnosis Form */}
                                    {currentModalStep === 1 && (
                                        <>
                                            {/* Service Results */}
                                            <div className="row mb-4">
                                                <div className="col-12">
                                                    <h6 className="fw-bold text-success mb-3">
                                                        <i className="fas fa-clipboard-list me-2"></i>
                                                        Kết quả dịch vụ đã thực hiện
                                                    </h6>
                                            <div className="table-responsive">
                                                <table className="table table-bordered">
                                                    <thead className="table-success">
                                                        <tr>
                                                            <th>STT</th>
                                                            <th>Tên dịch vụ</th>
                                                            <th>Kết quả</th>
                                                            <th>Bác sĩ thực hiện</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {getServiceOrderItemsForOrder(selectedServiceOrder.id).map((item, index) => {
                                                            const service = getServiceInfo(item.service_id);
                                                            const result = serviceResults.find(r => r.service_order_item_id === item.id);
                                                            
                                                            return (
                                                                <tr key={item.id}>
                                                                    <td>{index + 1}</td>
                                                                    <td>
                                                                        <strong>{service.name || 'Dịch vụ không xác định'}</strong>
                                                                        <br />
                                                                        <small className="text-muted">{service.description || ''}</small>
                                                                    </td>
                                                                    <td>
                                                                        {result ? (
                                                                            <div className="text-wrap" style={{maxWidth: '300px'}}>
                                                                                {result.result_description}
                                                                            </div>
                                                                        ) : (
                                                                            <span className="text-muted">Chưa có kết quả</span>
                                                                        )}
                                                                    </td>
                                                                    <td>
                                                                        <span className="badge bg-primary">
                                                                            BS {item.doctor_id}
                                                                        </span>
                                                                    </td>
                                                                </tr>
                                                            );
                                                        })}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Diagnosis Form */}
                                    <form onSubmit={handleDiagnosisSubmit}>
                                        <div className="row">
                                            <div className="col-12">
                                                <h6 className="fw-bold text-warning mb-3">
                                                    <i className="fas fa-edit me-2"></i>
                                                    Nhập kết luận điều trị
                                                </h6>
                                            </div>
                                            <div className="col-md-6 mb-3">
                                                <label className="form-label">
                                                    <strong>Chẩn đoán bệnh:</strong>
                                                </label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={diagnosisForm.disease}
                                                    onChange={(e) => setDiagnosisForm({...diagnosisForm, disease: e.target.value})}
                                                    placeholder="Nhập tên bệnh..."
                                                    required
                                                />
                                            </div>
                                            <div className="col-md-6 mb-3">
                                                <label className="form-label">
                                                    <strong>Kế hoạch điều trị:</strong>
                                                </label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={diagnosisForm.treatment_plan}
                                                    onChange={(e) => setDiagnosisForm({...diagnosisForm, treatment_plan: e.target.value})}
                                                    placeholder="Nhập kế hoạch điều trị..."
                                                    required
                                                />
                                            </div>
                                            <div className="col-12 mb-3">
                                                <label className="form-label">
                                                    <strong>Kết luận chi tiết:</strong>
                                                </label>
                                                <textarea
                                                    className="form-control"
                                                    rows="4"
                                                    value={diagnosisForm.conclusion}
                                                    onChange={(e) => setDiagnosisForm({...diagnosisForm, conclusion: e.target.value})}
                                                    placeholder="Nhập kết luận chi tiết dựa trên kết quả các dịch vụ đã thực hiện..."
                                                    required
                                                ></textarea>
                                            </div>
                                        </div>
                                    </form>
                                        </>
                                    )}

                                    {/* Step 2: Prescription Form */}
                                    {currentModalStep === 2 && (
                                        <div className="row">
                                            <div className="col-12">
                                                <h6 className="fw-bold text-primary mb-3">
                                                    <i className="fas fa-pills me-2"></i>
                                                    Kê đơn thuốc điều trị
                                                </h6>
                                                
                                                {/* Selected Medicines Summary */}
                                                {selectedMedicines.length > 0 && (
                                                    <div className="alert alert-success">
                                                        <h6 className="mb-2">
                                                            <i className="fas fa-check-circle me-2"></i>
                                                            Thuốc đã chọn ({selectedMedicines.length})
                                                        </h6>
                                                        {selectedMedicines.map((item) => {
                                                            const medicine = medicines.find(m => m.id === item.medicineId);
                                                            return (
                                                                <div key={item.medicineId} className="d-flex justify-content-between align-items-center mb-2">
                                                                    <div>
                                                                        <strong>{medicine?.name}</strong>
                                                                        <br />
                                                                        <small className="text-muted">
                                                                            SL: {item.quantity} | Liều: {item.dosage}
                                                                        </small>
                                                                    </div>
                                                                    <span className="badge bg-primary">
                                                                        {getMedicinePrice(medicine)}
                                                                    </span>
                                                                </div>
                                                            );
                                                        })}
                                                    </div>
                                                )}

                                                {/* Medicine Selection Table */}
                                                <div className="table-responsive" style={{maxHeight: '400px', overflowY: 'auto'}}>
                                                    <table className="table table-hover">
                                                        <thead className="table-light sticky-top">
                                                            <tr>
                                                                <th width="50">Chọn</th>
                                                                <th>Tên Thuốc</th>
                                                                <th>Thành Phần</th>
                                                                <th>Công Dụng</th>
                                                                <th>Giá</th>
                                                                <th>Số Lượng</th>
                                                                <th>Liều Dùng</th>
                                                            </tr>
                                                        </thead>
                                                        <tbody>
                                                            {medicines.map((medicine) => {
                                                                const isSelected = selectedMedicines.some(item => item.medicineId === medicine.id);
                                                                const selectedMedicine = selectedMedicines.find(item => item.medicineId === medicine.id);
                                                                
                                                                return (
                                                                    <tr key={medicine.id} className={isSelected ? 'table-warning' : ''}>
                                                                        <td>
                                                                            <input
                                                                                type="checkbox"
                                                                                className="form-check-input"
                                                                                checked={isSelected}
                                                                                onChange={() => handleMedicineToggle(medicine.id)}
                                                                            />
                                                                        </td>
                                                                        <td>
                                                                            <strong>{medicine.name}</strong>
                                                                        </td>
                                                                        <td>
                                                                            <small className="text-muted">{medicine.ingredient}</small>
                                                                        </td>
                                                                        <td>
                                                                            <small className="text-muted">{medicine.usage}</small>
                                                                        </td>
                                                                        <td>
                                                                            <span className="badge bg-info">
                                                                                {getMedicinePrice(medicine)}
                                                                            </span>
                                                                        </td>
                                                                        <td>
                                                                            {isSelected ? (
                                                                                <input
                                                                                    type="number"
                                                                                    className="form-control form-control-sm"
                                                                                    style={{width: '80px'}}
                                                                                    min="1"
                                                                                    value={selectedMedicine?.quantity || 1}
                                                                                    onChange={(e) => handleMedicineChange(medicine.id, 'quantity', parseInt(e.target.value))}
                                                                                />
                                                                            ) : (
                                                                                <span className="text-muted">--</span>
                                                                            )}
                                                                        </td>
                                                                        <td>
                                                                            {isSelected ? (
                                                                                <input
                                                                                    type="text"
                                                                                    className="form-control form-control-sm"
                                                                                    style={{width: '150px'}}
                                                                                    placeholder="1 viên x 3 lần/ngày"
                                                                                    value={selectedMedicine?.dosage || ''}
                                                                                    onChange={(e) => handleMedicineChange(medicine.id, 'dosage', e.target.value)}
                                                                                />
                                                                            ) : (
                                                                                <span className="text-muted">--</span>
                                                                            )}
                                                                        </td>
                                                                    </tr>
                                                                );
                                                            })}
                                                        </tbody>
                                                    </table>
                                                </div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                                <div className="modal-footer">
                                    {currentModalStep === 1 ? (
                                        <>
                                            <button 
                                                type="button" 
                                                className="btn btn-secondary"
                                                onClick={() => setShowDiagnosisModal(false)}
                                                disabled={diagnosisSubmitting}
                                            >
                                                <i className="fas fa-times me-2"></i>
                                                Hủy
                                            </button>
                                            <button 
                                                type="button"
                                                className="btn btn-warning"
                                                onClick={handleDiagnosisSubmit}
                                                disabled={diagnosisSubmitting}
                                            >
                                                {diagnosisSubmitting ? (
                                                    <>
                                                        <div className="spinner-border spinner-border-sm me-2" role="status">
                                                            <span className="visually-hidden">Đang xử lý...</span>
                                                        </div>
                                                        Đang lưu...
                                                    </>
                                                ) : (
                                                    <>
                                                        <i className="fas fa-arrow-right me-2"></i>
                                                        Tiếp theo: Kê đơn thuốc
                                                    </>
                                                )}
                                            </button>
                                        </>
                                    ) : (
                                        <>
                                            <button 
                                                type="button" 
                                                className="btn btn-secondary"
                                                onClick={() => setCurrentModalStep(1)}
                                                disabled={diagnosisSubmitting}
                                            >
                                                <i className="fas fa-arrow-left me-2"></i>
                                                Quay lại
                                            </button>
                                            <button 
                                                type="button" 
                                                className="btn btn-outline-success"
                                                onClick={handleSkipPrescription}
                                                disabled={diagnosisSubmitting}
                                            >
                                                <i className="fas fa-check me-2"></i>
                                                Bỏ qua - Hoàn thành
                                            </button>
                                            <button 
                                                type="button"
                                                className="btn btn-success"
                                                onClick={handlePrescriptionSubmit}
                                                disabled={diagnosisSubmitting || selectedMedicines.length === 0}
                                            >
                                                {diagnosisSubmitting ? (
                                                    <>
                                                        <div className="spinner-border spinner-border-sm me-2" role="status">
                                                            <span className="visually-hidden">Đang xử lý...</span>
                                                        </div>
                                                        Đang kê đơn...
                                                    </>
                                                ) : (
                                                    <>
                                                        <i className="fas fa-prescription-bottle me-2"></i>
                                                        Kê đơn thuốc ({selectedMedicines.length})
                                                    </>
                                                )}
                                            </button>
                                        </>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default DoctorDashboard;
