                        <label className="form-label fw-semibold">üî¢ S·ªë l∆∞·ª£ng</label>
                        <input
                            type="number"
                            name="quantity"
                            value={medicine.quantity}
                            className="form-control rounded-3 shadow-sm"
                            onChange={handleChange}
                            required
