import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import imageCompression from 'browser-image-compression';

const EditPatient = ({ patientId = '1', onSave = () => {}, onCancel = () => {} }) => {
  const [formData, setFormData] = useState({
    full_name: '',
    email: '',
    dob: '',
    bio: '',
    img: null,
    imgPreview: 'default.png',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`);
        if (!patientResponse.ok) {
          throw new Error('Không thể tải thông tin bệnh nhân');
        }
        const patientData = await patientResponse.json();

        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`);
        if (!accountResponse.ok) {
          throw new Error('Không thể tải thông tin tài khoản');
        }
        const accountData = await accountResponse.json();

        setFormData({
          full_name: patientData.full_name || '',
          email: accountData.email || '',
          dob: patientData.dob || '',
          bio: patientData.address || '', // Giả sử address làm bio
          img: null,
          imgPreview: accountData.img || 'default.png',
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [patientId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        const options = {
          maxSizeMB: 1,
          maxWidthOrHeight: 350, // Đảm bảo ảnh không vượt quá 350x350
          useWebWorker: true,
        };
        const compressedFile = await imageCompression(file, options);
        setFormData((prev) => ({
          ...prev,
          img: compressedFile,
          imgPreview: URL.createObjectURL(compressedFile),
        }));
      } catch (err) {
        setError('Không thể xử lý ảnh: ' + err.message);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: patientId,
          full_name: formData.full_name,
          dob: formData.dob,
          gender: '', // Không có trong thiết kế, để trống
          phone: '', // Không có trong thiết kế, để trống
          address: formData.bio, // Sử dụng bio làm address
        }),
      });

      if (!patientResponse.ok) {
        throw new Error('Không thể cập nhật thông tin bệnh nhân');
      }

      if (formData.img) {
        const formDataToSend = new FormData();
        formDataToSend.append('img', formData.img);
        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`, {
          method: 'PUT',
          body: formDataToSend,
        });

        if (!accountResponse.ok) {
          throw new Error('Không thể cập nhật ảnh tài khoản');
        }
      }

      const updatedPatient = await patientResponse.json();
      onSave(updatedPatient);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center h-100">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger text-center p-4 mx-auto" style={{ maxWidth: '700px' }} role="alert">
        <strong>Lỗi:</strong> {error}
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <div className="card shadow-sm border-0 rounded-4 mx-auto" style={{ maxWidth: '800px' }}>
        <div className="card-body p-4">
          <div className="row g-4">
            <div className="col-md-4 text-center">
              <div className="position-relative">
                <img
                  src={formData.imgPreview}
                  alt="Ảnh đại diện"
                  className="img-fluid rounded-3"
                  style={{ width: '350px', height: '350px', objectFit: 'cover' }}
                />
                {formData.imgPreview === 'default.png' && (
                  <div className="position-absolute top-50 start-50 translate-middle text-dark" style={{ fontSize: '2rem' }}>
                    350x350
                  </div>
                )}
              </div>
              <p className="mt-3 mb-1 text-muted">Amine Steward</p>
              <button
                type="button"
                className="btn btn-link text-decoration-none text-primary"
                onClick={() => document.getElementById('img').click()}
              >
                Upload A New Image
              </button>
              <input
                type="file"
                id="img"
                name="img"
                accept="image/*"
                onChange={handleImageChange}
                className="d-none"
              />
            </div>
            <div className="col-md-8">
              <form onSubmit={handleSubmit}>
                <div className="row g-3">
                  <div className="col-12">
                    <label htmlFor="full_name" className="form-label fw-semibold text-dark">
                      Your Full Name *
                    </label>
                    <input
                      type="text"
                      id="full_name"
                      name="full_name"
                      value={formData.full_name}
                      onChange={handleChange}
                      className="form-control form-control-lg rounded-3"
                      required
                    />
                  </div>
                  <div className="col-6">
                    <label htmlFor="email" className="form-label fw-semibold text-dark">
                      E-mail Address *
                    </label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      className="form-control form-control-lg rounded-3"
                      required
                    />
                  </div>
                  <div className="col-6">
                    <label htmlFor="dob" className="form-label fw-semibold text-dark">
                      Your DOB *
                    </label>
                    <input
                      type="date"
                      id="dob"
                      name="dob"
                      value={formData.dob}
                      onChange={handleChange}
                      className="form-control form-control-lg rounded-3"
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label htmlFor="bio" className="form-label fw-semibold text-dark">
                      Your Bio
                    </label>
                    <textarea
                      id="bio"
                      name="bio"
                      value={formData.bio}
                      onChange={handleChange}
                      className="form-control form-control-lg rounded-3"
                      rows="4"
                    />
                  </div>
                </div>
                <div className="d-flex justify-content-end mt-4">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg px-4 rounded-3"
                    disabled={loading}
                  >
                    Save Changes <i className="bi bi-plus-lg"></i>
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .card {
          background: #f8f9fa;
        }
        .img-fluid {
          border: 1px solid #dee2e6;
        }
        .btn-primary {
          background-color: #6c757d;
          border-color: #6c757d;
        }
        .btn-primary:hover:enabled {
          background-color: #5a6268;
          border-color: #5a6268;
        }
        .btn-primary:disabled {
          background-color: #ced4da;
          border-color: #ced4da;
          cursor: not-allowed;
        }
        .form-control-lg {
          background-color: #e9ecef;
          border: none;
          transition: background-color 0.3s ease;
        }
        .form-control-lg:focus {
          background-color: #ffffff;
          box-shadow: 0 0 0 0.2rem rgba(108, 117, 125, 0.25);
        }
        .form-control-lg:hover {
          background-color: #dee2e6;
        }
      `}</style>
    </div>
  );
};

EditPatient.propTypes = {
  patientId: PropTypes.string,
  onSave: PropTypes.func,
  onCancel: PropTypes.func,
};

EditPatient.defaultProps = {
  patientId: '1',
  onSave: () => {},
  onCancel: () => {},
};

export default EditPatient;
