import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DoctorHeader from './DoctorHeader';
import { 
    getWaitlistByDoctorId, 
    getPatients, 
    getRooms,
    createExamResult,
    updateWaitlistStatus,
    createMedicineRecord,
    getExamResults,
    getExamResultsByDoctorId,
    getExamResultByMedicineRecordId,
    getMedicineRecords,
    getServiceOrdersByDoctorId,
    getServiceOrderItems,
    getResultsOfParaclinicalServices,
    getMedicalServices,
    getDiagnosisByMedicineRecordId,
    createDiagnosis,
    getMedicines,
    createPrescription,
    createPrescriptionInvoice,
    createMedicineDetail
} from '../services/api';

const DoctorDashboard = () => {
    const navigate = useNavigate();
    
    const [waitlist, setWaitlist] = useState([]);
    const [allWaitlist, setAllWaitlist] = useState([]); // For statistics
    const [patients, setPatients] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [examResults, setExamResults] = useState([]);
    const [medicineRecords, setMedicineRecords] = useState([]);
    const [serviceOrders, setServiceOrders] = useState([]);
    const [serviceOrderItems, setServiceOrderItems] = useState([]);
    const [serviceResults, setServiceResults] = useState([]);
    const [medicalServices, setMedicalServices] = useState([]);
    const [medicines, setMedicines] = useState([]);
    const [diagnosisData, setDiagnosisData] = useState([]); // Add diagnosis state
    const [loading, setLoading] = useState(true);
    const [currentDoctorId, setCurrentDoctorId] = useState('1'); // Mock doctor ID - should come from auth
    
    // Exam Modal States
    const [showExamModal, setShowExamModal] = useState(false);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [selectedWaitlistItem, setSelectedWaitlistItem] = useState(null);
    const [examForm, setExamForm] = useState({
        symptoms: '',
        preliminary_diagnosis: '',
        notes: ''
    });
    const [submitting, setSubmitting] = useState(false);

    // Diagnosis Modal States
    const [showDiagnosisModal, setShowDiagnosisModal] = useState(false);
    const [selectedServiceOrder, setSelectedServiceOrder] = useState(null);
    const [diagnosisForm, setDiagnosisForm] = useState({
        conclusion: '',
        disease: '',
        treatment_plan: ''
    });
    const [diagnosisSubmitting, setDiagnosisSubmitting] = useState(false);
    
    // Prescription States (within diagnosis modal)
    const [selectedMedicines, setSelectedMedicines] = useState([]);
    const [currentModalStep, setCurrentModalStep] = useState(1); // 1: Diagnosis, 2: Prescription

    // Fetch data on component mount
    useEffect(() => {
        fetchDashboardData();
    }, [currentDoctorId]);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            const [waitlistRes, patientsRes, roomsRes, examResultsRes, medicineRecordsRes, serviceOrdersRes, serviceOrderItemsRes, serviceResultsRes, medicalServicesRes, medicinesRes] = await Promise.all([
                getWaitlistByDoctorId(currentDoctorId),
                getPatients(),
                getRooms(),
                getExamResultsByDoctorId(currentDoctorId),
                getMedicineRecords(),
                getServiceOrdersByDoctorId(currentDoctorId),
                getServiceOrderItems(),
                getResultsOfParaclinicalServices(),
                getMedicalServices(),
                getMedicines()
            ]);

            if (waitlistRes.success) {
                // Hiá»ƒn thá»‹ bá»‡nh nhÃ¢n Ä‘ang chá» khÃ¡m vÃ  Ä‘ang Ä‘iá»u trá»‹ (Ä‘á»ƒ chá»‰ Ä‘á»‹nh dá»‹ch vá»¥)
                setWaitlist(waitlistRes.data.filter(item => item.status === 'Waiting' || item.status === 'InProgress'));
                setAllWaitlist(waitlistRes.data);
            }

            if (patientsRes.success) {
                setPatients(patientsRes.data);
            }

            if (roomsRes.success) {
                setRooms(roomsRes.data);
            }

            if (examResultsRes.success) {
                setExamResults(examResultsRes.data);
            }

            if (medicineRecordsRes.success) {
                setMedicineRecords(medicineRecordsRes.data);
            }

            if (serviceOrdersRes.success) {
                setServiceOrders(serviceOrdersRes.data);
            }

            if (serviceOrderItemsRes.success) {
                setServiceOrderItems(serviceOrderItemsRes.data);
            }

            if (serviceResultsRes.success) {
                setServiceResults(serviceResultsRes.data);
            }

            if (medicalServicesRes.success) {
                setMedicalServices(medicalServicesRes.data);
            }

            if (medicinesRes.success) {
                setMedicines(medicinesRes.data);
            }

            // Load diagnosis data for each service order
            if (serviceOrdersRes.success) {
                const diagnosisPromises = serviceOrdersRes.data.map(async (order) => {
                    const diagnosisRes = await getDiagnosisByMedicineRecordId(order.medicineRecord_id);
                    return {
                        serviceOrderId: order.id,
                        medicineRecordId: order.medicineRecord_id,
                        diagnosis: diagnosisRes.success ? diagnosisRes.data : []
                    };
                });

                const diagnosisResults = await Promise.all(diagnosisPromises);
                setDiagnosisData(diagnosisResults);
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            alert('âŒ CÃ³ lá»—i xáº£y ra khi táº£i dá»¯ liá»‡u!');
        } finally {
            setLoading(false);
        }
    };

    // Get patient info by ID
    const getPatientInfo = (patientId) => {
        return patients.find(p => p.id === patientId) || {};
    };

    // Get room info by ID
    const getRoomInfo = (roomId) => {
        return rooms.find(r => r.id === roomId) || {};
    };

    // Format time display
    const formatTime = (timeString) => {
        return new Date(timeString).toLocaleTimeString('vi-VN', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    // Handle start examination
    const handleStartExam = (waitlistItem, isAssignment = false) => {
        const patient = getPatientInfo(waitlistItem.patient_id);
        
        if (isAssignment) {
            // Navigate to service assignment page
            navigate(`/service-assignment/${waitlistItem.patient_id}/${waitlistItem.id}`);
        } else {
            // Open exam modal
            setSelectedWaitlistItem(waitlistItem);
            setSelectedPatient(patient);
            setShowExamModal(true);
            setExamForm({
                symptoms: '',
                preliminary_diagnosis: '',
                notes: ''
            });
        }
    };

    // Handle exam form submission
    const handleExamSubmit = async (e) => {
        e.preventDefault();
        
        if (!examForm.symptoms.trim() || !examForm.preliminary_diagnosis.trim()) {
            alert('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ triá»‡u chá»©ng vÃ  cháº©n Ä‘oÃ¡n sÆ¡ bá»™!');
            return;
        }

        setSubmitting(true);

        try {
            // Always create NEW medicine record for each examination visit
            // Má»—i láº§n khÃ¡m táº¡o má»™t há»“ sÆ¡ bá»‡nh Ã¡n má»›i Ä‘á»ƒ trÃ¡nh bá»‹ láº«n lá»™n dá»¯ liá»‡u
            const newRecordRes = await createMedicineRecord({
                patient_id: selectedPatient.id
            });
            
            let medicineRecordId;
            if (newRecordRes.success) {
                medicineRecordId = newRecordRes.data.id;
            } else {
                throw new Error('KhÃ´ng thá»ƒ táº¡o há»“ sÆ¡ bá»‡nh Ã¡n má»›i cho láº§n khÃ¡m nÃ y');
            }

            // Create exam result
            const examData = {
                medicineRecord_id: medicineRecordId,
                symptoms: examForm.symptoms,
                preliminary_diagnosis: examForm.preliminary_diagnosis,
                doctor_id: currentDoctorId,
                notes: examForm.notes || ''
            };

            const examResultRes = await createExamResult(examData);
            
            if (examResultRes.success) {
                // Update waitlist status to InProgress (Ä‘á»ƒ cÃ³ thá»ƒ chá»‰ Ä‘á»‹nh dá»‹ch vá»¥)
                const updateRes = await updateWaitlistStatus(selectedWaitlistItem.id, 'InProgress');
                
                if (updateRes.success) {
                    alert('âœ… ÄÃ£ hoÃ n thÃ nh khÃ¡m lÃ¢m sÃ ng! CÃ³ thá»ƒ chá»‰ Ä‘á»‹nh dá»‹ch vá»¥.');
                    setShowExamModal(false);
                    fetchDashboardData(); // Refresh all data including exam results by doctor
                } else {
                    alert('ÄÃ£ lÆ°u káº¿t quáº£ khÃ¡m nhÆ°ng khÃ´ng thá»ƒ cáº­p nháº­t tráº¡ng thÃ¡i!');
                }
            } else {
                throw new Error('KhÃ´ng thá»ƒ lÆ°u káº¿t quáº£ khÃ¡m');
            }

        } catch (error) {
            console.error('Error saving exam result:', error);
            alert('âŒ CÃ³ lá»—i xáº£y ra khi lÆ°u káº¿t quáº£ khÃ¡m: ' + error.message);
        } finally {
            setSubmitting(false);
        }
    };

    // Get visit type text in Vietnamese
    const getVisitTypeText = (visitType) => {
        switch(visitType) {
            case 'Initial': return 'KhÃ¡m láº§n Ä‘áº§u';
            case 'Result': return 'Xem káº¿t quáº£';
            default: return visitType;
        }
    };

    // Get status badge for waitlist
    const getStatusBadge = (status) => {
        switch(status) {
            case 'Waiting': 
                return <span className="badge bg-info">Chá» khÃ¡m</span>;
            case 'InProgress': 
                return <span className="badge bg-warning">Äang Ä‘iá»u trá»‹</span>;
            case 'Complete': 
                return <span className="badge bg-success">HoÃ n thÃ nh</span>;
            default: 
                return <span className="badge bg-secondary">{status}</span>;
        }
    };

    // Check if patient has exam result
    const hasExamResult = (patientId) => {
        const medicineRecord = medicineRecords.find(record => record.patient_id === patientId);
        if (!medicineRecord) return false;
        
        const examResult = examResults.find(result => result.medicineRecord_id === medicineRecord.id);
        return !!examResult;
    };

    // Get exam result for patient
    const getExamResultForPatient = (patientId) => {
        const medicineRecord = medicineRecords.find(record => record.patient_id === patientId);
        if (!medicineRecord) return null;
        
        return examResults.find(result => result.medicineRecord_id === medicineRecord.id);
    };

    // Refresh only exam results for better performance
    const refreshExamResults = async () => {
        try {
            const examResultsRes = await getExamResultsByDoctorId(currentDoctorId);
            if (examResultsRes.success) {
                setExamResults(examResultsRes.data);
            }
        } catch (error) {
            console.error('Error refreshing exam results:', error);
        }
    };

    // Get service order items for a specific order
    const getServiceOrderItemsForOrder = (serviceOrderId) => {
        return serviceOrderItems.filter(item => item.service_order_id === serviceOrderId);
    };

    // Get service results for a specific order
    const getServiceResultsForOrder = (serviceOrderId) => {
        const orderItems = getServiceOrderItemsForOrder(serviceOrderId);
        const orderItemIds = orderItems.map(item => item.id);
        return serviceResults.filter(result => orderItemIds.includes(result.service_order_item_id));
    };

    // Check if service order is fully completed (all services have results)
    const isServiceOrderCompleted = (serviceOrder) => {
        const orderItems = getServiceOrderItemsForOrder(serviceOrder.id);
        const results = getServiceResultsForOrder(serviceOrder.id);
        return orderItems.length > 0 && results.length === orderItems.length;
    };

    // Check if service order has diagnosis
    const hasServiceOrderDiagnosis = (serviceOrderId) => {
        const diagnosisEntry = diagnosisData.find(entry => entry.serviceOrderId === serviceOrderId);
        return diagnosisEntry && diagnosisEntry.diagnosis.length > 0;
    };

    // Get completed service orders that don't have diagnosis yet
    const getCompletedServiceOrdersWithoutDiagnosis = () => {
        console.log('ðŸ” Debugging Service Orders:');
        console.log('Total service orders:', serviceOrders.length);
        console.log('Service orders:', serviceOrders);
        
        const filtered = serviceOrders.filter(order => {
            const orderItems = getServiceOrderItemsForOrder(order.id);
            const results = getServiceResultsForOrder(order.id);
            const isCompleted = isServiceOrderCompleted(order);
            const hasDiagnosis = hasServiceOrderDiagnosis(order.id);
            
            console.log(`Order ${order.id}:`, {
                orderItems: orderItems.length,
                results: results.length,
                isCompleted,
                hasDiagnosis
            });
            
            return isCompleted && !hasDiagnosis;
        });
        
        console.log('Completed orders without diagnosis:', filtered.length);
        return filtered;
    };

    // Get completed service orders that already have diagnosis
    const getCompletedServiceOrdersWithDiagnosis = () => {
        return serviceOrders.filter(order => {
            const isCompleted = isServiceOrderCompleted(order);
            const hasDiagnosis = hasServiceOrderDiagnosis(order.id);
            return isCompleted && hasDiagnosis;
        });
    };

    // Get service info by ID
    const getServiceInfo = (serviceId) => {
        return medicalServices.find(s => s.id === serviceId) || {};
    };

    // Handle diagnosis modal
    const handleOpenDiagnosisModal = (serviceOrder) => {
        setSelectedServiceOrder(serviceOrder);
        setDiagnosisForm({
            conclusion: '',
            disease: '',
            treatment_plan: ''
        });
        setSelectedMedicines([]);
        setCurrentModalStep(1);
        setShowDiagnosisModal(true);
    };

    // Handle medicine selection
    const handleMedicineToggle = (medicineId) => {
        setSelectedMedicines(prev => {
            const existingIndex = prev.findIndex(item => item.medicineId === medicineId);
            
            if (existingIndex >= 0) {
                return prev.filter(item => item.medicineId !== medicineId);
            } else {
                return [...prev, { 
                    medicineId, 
                    quantity: 1, 
                    dosage: '1 viÃªn x 3 láº§n/ngÃ y' 
                }];
            }
        });
    };

    const handleMedicineChange = (medicineId, field, value) => {
        setSelectedMedicines(prev => 
            prev.map(item => 
                item.medicineId === medicineId 
                    ? { ...item, [field]: value }
                    : item
            )
        );
    };

    const getMedicinePrice = (medicine) => {
        return medicine.price && !isNaN(medicine.price) 
            ? new Intl.NumberFormat('vi-VN').format(medicine.price) + ' VNÄ'
            : 'LiÃªn há»‡';
    };

    // Handle diagnosis submission (step 1)
    const handleDiagnosisSubmit = async (e) => {
        e.preventDefault();
        
        if (!diagnosisForm.conclusion.trim() || !diagnosisForm.disease.trim() || !diagnosisForm.treatment_plan.trim()) {
            alert('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ thÃ´ng tin káº¿t luáº­n Ä‘iá»u trá»‹!');
            return;
        }

        setDiagnosisSubmitting(true);

        try {
            const diagnosisData = {
                doctor_id: currentDoctorId,
                medicineRecord_id: selectedServiceOrder.medicineRecord_id,
                conclusion: diagnosisForm.conclusion,
                disease: diagnosisForm.disease,
                treatment_plan: diagnosisForm.treatment_plan
            };

            const result = await createDiagnosis(diagnosisData);
            if (result.success) {
                // Refresh diagnosis data
                await fetchDashboardData();
                // Move to prescription step
                setCurrentModalStep(2);
                alert('âœ… LÆ°u káº¿t luáº­n Ä‘iá»u trá»‹ thÃ nh cÃ´ng! Tiáº¿n hÃ nh kÃª Ä‘Æ¡n thuá»‘c.');
            } else {
                throw new Error('KhÃ´ng thá»ƒ lÆ°u káº¿t luáº­n Ä‘iá»u trá»‹');
            }

        } catch (error) {
            console.error('Error creating diagnosis:', error);
            alert('âŒ CÃ³ lá»—i xáº£y ra khi lÆ°u káº¿t luáº­n Ä‘iá»u trá»‹!');
        } finally {
            setDiagnosisSubmitting(false);
        }
    };

    // Handle prescription submission (step 2)
    const handlePrescriptionSubmit = async () => {
        if (selectedMedicines.length === 0) {
            alert('Vui lÃ²ng chá»n Ã­t nháº¥t má»™t loáº¡i thuá»‘c hoáº·c bá» qua Ä‘á»ƒ hoÃ n thÃ nh!');
            return;
        }

        const invalidMedicines = selectedMedicines.filter(item => !item.quantity || !item.dosage);
        if (invalidMedicines.length > 0) {
            alert('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ sá»‘ lÆ°á»£ng vÃ  liá»u dÃ¹ng cho táº¥t cáº£ thuá»‘c!');
            return;
        }

        setDiagnosisSubmitting(true);

        try {
            // Create prescription
            const prescriptionData = {
                medicineRecord_id: selectedServiceOrder.medicineRecord_id,
                doctor_id: currentDoctorId,
                prescription_date: new Date().toISOString().split('T')[0],
                status: 'Pending'
            };

            const prescriptionRes = await createPrescription(prescriptionData);
            if (!prescriptionRes.success) {
                throw new Error('KhÃ´ng thá»ƒ táº¡o Ä‘Æ¡n thuá»‘c');
            }

            const prescriptionId = prescriptionRes.data.id;

            // Create prescription invoice
            const invoiceData = {
                invoice_id: `INV${Date.now()}`,
                pharmacist_id: '1',
                prescription_id: prescriptionId
            };

            const invoiceRes = await createPrescriptionInvoice(invoiceData);
            if (!invoiceRes.success) {
                throw new Error('KhÃ´ng thá»ƒ táº¡o hÃ³a Ä‘Æ¡n thuá»‘c');
            }

            const prescriptionInvoiceId = invoiceRes.data.id;

            // Create medicine details
            for (const medicine of selectedMedicines) {
                const medicineDetailData = {
                    prescription_invoice_id: prescriptionInvoiceId,
                    medicine_id: medicine.medicineId,
                    quantity: medicine.quantity.toString(),
                    dosage: medicine.dosage
                };

                const detailRes = await createMedicineDetail(medicineDetailData);
                if (!detailRes.success) {
                    console.error('Failed to create medicine detail:', medicine);
                }
            }

            // Update waitlist status to Complete (hoÃ n thÃ nh toÃ n bá»™ quy trÃ¬nh Ä‘iá»u trá»‹)
            const waitlistItem = findWaitlistByMedicineRecord(selectedServiceOrder.medicineRecord_id);
            if (waitlistItem) {
                await updateWaitlistStatus(waitlistItem.id, 'Complete');
            }

            alert('âœ… KÃª Ä‘Æ¡n thuá»‘c thÃ nh cÃ´ng! HoÃ n thÃ nh quy trÃ¬nh Ä‘iá»u trá»‹.');
            setShowDiagnosisModal(false);
            await fetchDashboardData();

        } catch (error) {
            console.error('Error creating prescription:', error);
            alert('âŒ CÃ³ lá»—i xáº£y ra khi kÃª Ä‘Æ¡n thuá»‘c: ' + error.message);
        } finally {
            setDiagnosisSubmitting(false);
        }
    };

    // Find waitlist item by medicine record ID
    const findWaitlistByMedicineRecord = (medicineRecordId) => {
        const medicineRecord = medicineRecords.find(record => record.id === medicineRecordId);
        if (!medicineRecord) return null;
        
        // TÃ¬m waitlist item InProgress gáº§n nháº¥t cá»§a bá»‡nh nhÃ¢n (láº§n khÃ¡m hiá»‡n táº¡i)
        return allWaitlist
            .filter(waitlistItem => 
                waitlistItem.patient_id === medicineRecord.patient_id && 
                waitlistItem.status === 'InProgress'
            )
            .sort((a, b) => new Date(b.registered_at) - new Date(a.registered_at))[0];
    };

    // Skip prescription step
    const handleSkipPrescription = async () => {
        try {
            // Update waitlist status to Complete (hoÃ n thÃ nh toÃ n bá»™ quy trÃ¬nh Ä‘iá»u trá»‹)
            const waitlistItem = findWaitlistByMedicineRecord(selectedServiceOrder.medicineRecord_id);
            if (waitlistItem) {
                await updateWaitlistStatus(waitlistItem.id, 'Complete');
            }
            
            alert('âœ… HoÃ n thÃ nh káº¿t luáº­n Ä‘iá»u trá»‹ (khÃ´ng kÃª Ä‘Æ¡n thuá»‘c).');
            setShowDiagnosisModal(false);
            await fetchDashboardData();
        } catch (error) {
            console.error('Error updating waitlist status:', error);
            alert('âœ… HoÃ n thÃ nh káº¿t luáº­n Ä‘iá»u trá»‹ (khÃ´ng kÃª Ä‘Æ¡n thuá»‘c).');
            setShowDiagnosisModal(false);
            await fetchDashboardData();
        }
    };

    if (loading) {
