import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const sampleUserData = {
  Patient: [
    {
      id: 5,
      full_name: "Vũ Trung Hiếu",
      dob: "2001-11-02",
      gender: "Nam",
      phone: "0939432288",
      address: "4 Trần Hưng Đạo",
    },
  ],
  Doctor: [
    {
      id: 69,
      full_name: "Phạm Thị Mai",
      department: "Nội tiết",
      phone: "0903001098",
      eduLevel: "Thạc sĩ",
      account_staff_id: "99",
    },
  ],
  Pharmacist: [
    {
      id: 50,
      full_name: "Phạm Minh Trang",
      phone: "0937010050",
      eduLevel: "Cao đẳng",
      account_pharmacist_id: "50",
    },
  ],
};

function EditUserForm() {
  const [userType, setUserType] = useState("Patient");
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    full_name: "",
    dob: "",
    gender: "",
    phone: "",
    address: "",
    department: "",
    eduLevel: "",
    account_staff_id: "",
    account_pharmacist_id: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    // Simulate fetching user data based on ID and type
    const userId = 5; // Replace with dynamic ID from props or URL
    const type = "Patient"; // Replace with dynamic type (Patient, Doctor, Pharmacist)
    setUserType(type);

    let fetchedUser;
    if (type === "Patient") {
      fetchedUser = sampleUserData.Patient.find((u) => u.id === userId);
    } else if (type === "Doctor") {
      fetchedUser = sampleUserData.Doctor.find((u) => u.id === userId);
    } else if (type === "Pharmacist") {
      fetchedUser = sampleUserData.Pharmacist.find((u) => u.id === userId);
    }

    if (fetchedUser) {
      setUser(fetchedUser);
      setFormData({
        full_name: fetchedUser.full_name || "",
        dob: fetchedUser.dob || "",
        gender: fetchedUser.gender || "",
        phone: fetchedUser.phone || "",
        address: fetchedUser.address || "",
        department: fetchedUser.department || "",
        eduLevel: fetchedUser.eduLevel || "",
        account_staff_id: fetchedUser.account_staff_id || "",
        account_pharmacist_id: fetchedUser.account_pharmacist_id || "",
      });
    } else {
      setError("User not found");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Basic validation
    if (!formData.full_name || !formData.phone) {
      setError("Full name and phone are required");
      return;
    }
    if (!/^\d{10}$/.test(formData.phone)) {
      setError("Phone number must be 10 digits");
      return;
    }
    if (userType === "Patient") {
      if (!formData.dob) {
        setError("Date of birth is required for patients");
        return;
      }
      if (!formData.gender) {
        setError("Gender is required for patients");
        return;
      }
    }
    if (userType === "Doctor" && !formData.department) {
      setError("Department is required for doctors");
      return;
    }
    if ((userType === "Doctor" || userType === "Pharmacist") && !formData.eduLevel) {
      setError("Education level is required");
      return;
    }
    if (userType === "Doctor" && !formData.account_staff_id) {
      setError("Account staff ID is required for doctors");
      return;
    }
    if (userType === "Pharmacist" && !formData.account_pharmacist_id) {
      setError("Account pharmacist ID is required for pharmacists");
      return;
    }

    // Simulate saving data
    console.log("Saving user data:", {
      ...formData,
      id: user?.id,
      type: userType,
    });
    alert("User information updated successfully!");
    setError("");
  };

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger alert-dismissible fade show" role="alert">
          <strong>Error:</strong> {error}
          <button
            type="button"
            className="close"
            data-dismiss="alert"
            aria-label="Close"
            onClick={() => setError("")}
          >
            <span aria-hidden="true">×</span>
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="container mt-5">
        <div className="card shadow-sm">
          <div className="card-body text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="sr-only">Loading...</span>
            </div>
            <p className="mt-2 text-muted">Loading user data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid bg-light min-vh-100 d-flex alig py-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-12 col-md-8 col-lg-6">
            <div className="card shadow-lg border-0">
              <div className="card-header bg-primary text-white text-center py-4">
                <h3 className="mb-0">
                  <i className="fas fa-user-edit mr-2"></i>
                  Edit {userType} Information
                </h3>
              </div>
              <div className="card-body p-5">
                <form onSubmit={handleSubmit}>
                  <div className="form-group mb-4">
                    <label htmlFor="full_name" className="font-weight-bold text-dark">
                      Full Name
                    </label>
                    <div className="input-group">
                      <div className="input-group-prepend">
                        <span className="input-group-text bg-white border-right-0">
                          <i className="fas fa-user"></i>
                        </span>
                      </div>
                      <input
                        type="text"
                        id="full_name"
                        name="full_name"
                        value={formData.full_name}
                        onChange={handleChange}
                        className="form-control border-left-0"
                        placeholder="Enter full name"
                        required
                      />
                    </div>
                  </div>
                  {userType === "Patient" && (
                    <>
                      <div className="form-group mb-4">
                        <label htmlFor="dob" className="font-weight-bold text-dark">
                          Date of Birth
                        </label>
                        <input
                          type="date"
                          id="dob"
                          name="dob"
                          value={formData.dob}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </div>
                      <div className="form-group mb-4">
                        <label htmlFor="gender" className="font-weight-bold text-dark">
                          Gender
                        </label>
                        <select
                          id="gender"
                          name="gender"
                          value={formData.gender}
                          onChange={handleChange}
                          className="form-control custom-select"
                          required
                        >
                          <option value="">Select gender</option>
                          <option value="Nam">Nam</option>
                          <option value="Nữ">Nữ</option>
                          <option value="Khác">Khác</option>
                        </select>
                      </div>
                      <div className="form-group mb-4">
                        <label htmlFor="address" className="font-weight-bold text-dark">
                          Address
                        </label>
                        <input
                          type="text"
                          id="address"
                          name="address"
                          value={formData.address}
                          onChange={handleChange}
                          className="form-control"
                          placeholder="Enter address"
                        />
                      </div>
                    </>
                  )}
                  <div className="form-group mb-4">
                    <label htmlFor="phone" className="font-weight-bold text-dark">
                      Phone
                    </label>
                    <div className="input-group">
                      <div className="input-group-prepend">
                        <span className="input-group-text bg-white border-right-0">
                          <i className="fas fa-phone"></i>
                        </span>
                      </div>
                      <input
                        type="text"
                        id="phone"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        className="form-control border-left-0"
                        placeholder="Enter phone number"
                        required
                      />
                    </div>
                  </div>
                  {userType === "Doctor" && (
                    <>
                      <div className="form-group mb-4">
                        <label htmlFor="department" className="font-weight-bold text-dark">
                          Department
                        </label>
                        <input
                          type="text"
                          id="department"
                          name="department"
                          value={formData.department}
                          onChange={handleChange}
                          className="form-control"
                          placeholder="Enter department"
                          required
                        />
                      </div>
                      <div className="form-group mb-4">
                        <label htmlFor="account_staff_id" className="font-weight-bold text-dark">
                          Account Staff ID
                        </label>
                        <input
                          type="text"
                          id="account_staff_id"
                          name="account_staff_id"
                          value={formData.account_staff_id}
                          onChange={handleChange}
                          className="form-control"
                          placeholder="Enter account staff ID"
                          required
                        />
                      </div>
                    </>
                  )}
                  {userType === "Pharmacist" && (
                    <div className="form-group mb-4">
                      <label htmlFor="account_pharmacist_id" className="font-weight-bold text-dark">
                        Account Pharmacist ID
                      </label>
                      <input
                        type="text"
                        id="account_pharmacist_id"
                        name="account_pharmacist_id"
                        value={formData.account_pharmacist_id}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Enter account pharmacist ID"
                        required
                      />
                    </div>
                  )}
                  {(userType === "Doctor" || userType === "Pharmacist") && (
                    <div className="form-group mb-4">
                      <label htmlFor="eduLevel" className="font-weight-bold text-dark">
                        Education Level
                      </label>
                      <select
                        id="eduLevel"
                        name="eduLevel"
                        value={formData.eduLevel}
                        onChange={handleChange}
                        className="form-control custom-select"
                        required
                      >
                        <option value="">Select education level</option>
                        <option value="Cao đẳng">Cao đẳng</option>
                        <option value="Cử nhân">Cử nhân</option>
                        <option value="Thạc sĩ">Thạc sĩ</option>
                        <option value="Tiến sĩ">Tiến sĩ</option>
                      </select>
                    </div>
                  )}
                  <button
                    type="submit"
                    className="btn btn-primary btn-block btn-lg mt-4 shadow-sm"
                  >
                    <i className="fas fa-save mr-2"></i>
                    Save Changes
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditUserForm;
