// src/components/ForgetPassword.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ForgetPassword = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const navigate = useNavigate();

  const handleReset = async (e) => {
    e.preventDefault();
    try {
      // Bước 1: Tìm tài khoản theo username + email
      const accRes = await fetch("http://localhost:9999/AccountPatient");
      const accData = await accRes.json();

      const matchedAcc = accData.find(
        (acc) => acc.username === username && acc.email === email
      );

      if (!matchedAcc) {
        return alert("Không tìm thấy tài khoản với tên đăng nhập và email đã nhập");
      }

      // Bước 2: Kiểm tra số điện thoại trong bảng Patient
      const patientRes = await fetch(`http://localhost:9999/Patient/${matchedAcc.id}`);
      const patient = await patientRes.json();

      if (!patient || patient.phone !== phone) {
        return alert("Số điện thoại không khớp với hồ sơ bệnh nhân");
      }

      // Bước 3: Cập nhật mật khẩu mới
      const updateRes = await fetch(`http://localhost:9999/AccountPatient/${matchedAcc.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ password: newPassword })
      });

      if (!updateRes.ok) throw new Error("Cập nhật mật khẩu thất bại");

      alert("Đặt lại mật khẩu thành công!");
      navigate("/login");
    } catch (err) {
      alert("Có lỗi xảy ra khi đặt lại mật khẩu: " + err.message);
    }
  };

  return (
    <div className="forget-password-form">
      <form onSubmit={handleReset}>
        <h2>Quên mật khẩu</h2>
        <label>Tên đăng nhập</label>
        <input value={username} onChange={(e) => setUsername(e.target.value)} required />

        <label>Email</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <label>Số điện thoại</label>
        <input value={phone} onChange={(e) => setPhone(e.target.value)} required />

        <label>Mật khẩu mới</label>
        <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />

        <button type="submit">Đặt lại mật khẩu</button>
      </form>
    </div>
  );
};

export default ForgetPassword;
