import { useState, useEffect } from "react";

const sampleUserData = {
  AccountPatient: [
    {
      id: 1,
      username: "phamvannha",
      email: "pham.nha@gmail.com",
      status: "Enable",x
    },
  ],
  AccountStaff: [
    {
      id: 1,
      username: "doquocdat",
      email: "dat.doctor01@health.vn",
      role: "Doctor",
      status: "Enable",
    },
  ],
};

function EditUserForm() {
  const [userType, setUserType] = useState("patient");

  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    fullName: "",
    dateOfBirth: "",
    gender: "",
    phone: "",
    address: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const userId = 1;
    const type = "patient";
    setUserType(type);

    let fetchedUser;
    if (type === "patient") {
      fetchedUser = sampleUserData.AccountPatient.find((u) => u.id === userId);
    } else {
      fetchedUser = sampleUserData.AccountStaff.find((u) => u.id === userId);
    }

    if (fetchedUser) {
      setUser(fetchedUser);
      setFormData({
        username: fetchedUser.username,
        email: fetchedUser.email,
        status: fetchedUser.status,
        role: fetchedUser.role || "Doctor",
      });
    } else {
      setError("User not found");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.username || !formData.email) {
      setError("Username and email are required");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      setError("Invalid email format");
      return;
    }

    console.log("Saving user data:", {
      ...formData,
      id: user?.id,
      type: userType,
    });
    alert("User information updated successfully!");
    setError("");
  };

  if (error) {
    return (
      <div className="container mt-5">
        <div
          className="alert alert-danger alert-dismissible fade show"
          role="alert"
        >
          <strong>Error:</strong> {error}
          <button
            type="button"
            className="close"
            data-dismiss="alert"
            aria-label="Close"
            onClick={() => setError("")}
          >
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="container mt-5">
        <div className="card shadow-sm">
          <div className="card-body text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="sr-only">Loading...</span>
            </div>
            <p className="mt-2 text-muted">Loading user data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid bg-light min-vh-100 d-flex align-items-center py-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-12 col-md-8 col-lg-6">
            <div className="card shadow-lg border-0">
              <div className="card-header bg-primary text-white text-center py-4">
                <h3 className="mb-0">
                  <i className="fas fa-user-edit mr-2"></i>
                  Edit {userType === "patient" ? "Patient" : "Staff"}{" "}
                  Information
                </h3>
              </div>
              <div className="card-body p-5">
                <form onSubmit={handleSubmit}>
                  <div className="form-group mb-4">
                    <label
                      htmlFor="username"
                      className="font-weight-bold text-dark"
                    >
                      Username
                    </label>
                    <div className="input-group">
                      <div className="input-group-prepend">
                        <span className="input-group-text bg-white border-right-0">
                          <i className="fas fa-user"></i>
                        </span>
                      </div>
                      <input
                        type="text"
                        id="username"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        className="form-control border-left-0"
                        placeholder="Enter username"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-group mb-4">
                    <label
                      htmlFor="email"
                      className="font-weight-bold text-dark"
                    >
                      Email
                    </label>
                    <div className="input-group">
                      <div className="input-group-prepend">
                        <span className="input-group-text bg-white border-right-0">
                          <i className="fas fa-envelope"></i>
                        </span>
                      </div>
                      <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="form-control border-left-0"
                        placeholder="Enter email"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-group mb-4">
                    <label
                      htmlFor="status"
                      className="font-weight-bold text-dark"
                    >
                      Status
                    </label>
                    <select
                      id="status"
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      className="form-control custom-select"
                    >
                      <option value="Enable">Enable</option>
                      <option value="Disable">Disable</option>
                    </select>
                  </div>
                  {userType === "staff" && (
                    <div className="form-group mb-4">
                      <label
                        htmlFor="role"
                        className="font-weight-bold text-dark"
                      >
                        Role
                      </label>
                      <select
                        id="role"
                        name="role"
                        value={formData.role}
                        onChange={handleChange}
                        className="form-control custom-select"
                      >
                        <option value="Doctor">Doctor</option>
                        <option value="Nurse">Nurse</option>
                        <option value="Receptionist">Receptionist</option>
                        <option value="AdminSys">AdminSys</option>
                        <option value="AdminBusiness">AdminBusiness</option>
                      </select>
                    </div>
                  )}
                  <button
                    type="submit"
                    className="btn btn-primary btn-block btn-lg mt-4 shadow-sm"
                  >
                    <i className="fas fa-save mr-2"></i>
                    Save Changes
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditUserForm;
