import React, { useState, useEffect } from 'react';

const DoctorDashboard = () => {
    const [currentTime, setCurrentTime] = useState(new Date());
    const [appointments, setAppointments] = useState([]);
    const [stats, setStats] = useState({
        todayPatients: 12,
        totalPatients: 847,
        pendingAppointments: 8,
        completedToday: 6
    });

    // Update time every minute
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 60000);
        return () => clearInterval(timer);
    }, []);

    // Mock data for appointments
    useEffect(() => {
        setAppointments([
            { id: 1, time: '09:00', patient: 'Nguyễn Văn An', type: 'Khám tổng quát', status: 'confirmed' },
            { id: 2, time: '09:30', patient: 'Trần Thị Bình', type: 'Tái khám', status: 'pending' },
            { id: 3, time: '10:00', patient: 'Lê Hoàng Cường', type: 'Khám chuyên khoa', status: 'confirmed' },
            { id: 4, time: '10:30', patient: 'Phạm Thị Dung', type: 'Khám tổng quát', status: 'completed' },
            { id: 5, time: '11:00', patient: 'Hoàng Văn Ê', type: 'Khám định kỳ', status: 'confirmed' }
        ]);
    }, []);

    const formatTime = (date) => {
        return date.toLocaleTimeString('vi-VN', { 
            hour: '2-digit', 
            minute: '2-digit',
            hour12: false 
        });
    };

    const formatDate = (date) => {
        return date.toLocaleDateString('vi-VN', { 
            weekday: 'long',
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
        });
    };

    const getStatusColor = (status) => {
        switch(status) {
            case 'completed': return '#10b981';
            case 'confirmed': return '#3b82f6';
            case 'pending': return '#f59e0b';
            default: return '#6b7280';
        }
    };

    const getStatusText = (status) => {
        switch(status) {
            case 'completed': return 'Hoàn thành';
            case 'confirmed': return 'Đã xác nhận';
            case 'pending': return 'Chờ xác nhận';
            default: return 'Không xác định';
        }
    };

    return (
        <div style={{ backgroundColor: '#f8fafc', minHeight: '100vh' }}>
            {/* Bootstrap CSS */}
            <link 
                href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" 
                rel="stylesheet"
            />
            <link 
                href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css" 
                rel="stylesheet"
            />

            <div className="container-fluid py-4">
                {/* Header Section */}
                <div className="row mb-4">
                    <div className="col-12">
                        <div className="d-flex justify-content-between align-items-center">
                            <div>
                                <h1 className="h3 fw-bold mb-1" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-user-md me-3" style={{ color: '#3b82f6' }}></i>
                                    Doctor Dashboard
                                </h1>
                                <p className="text-muted mb-0">
                                    <i className="fas fa-calendar-day me-2"></i>
                                    {formatDate(currentTime)}
                                </p>
                            </div>
                            <div className="text-end">
                                <div className="h4 fw-bold mb-0" style={{ color: '#3b82f6' }}>
                                    <i className="fas fa-clock me-2"></i>
                                    {formatTime(currentTime)}
                                </div>
                                <p className="text-muted mb-0">Thời gian hiện tại</p>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Stats Cards */}
                <div className="row mb-4">
                    {[
                        {
                            title: 'Bệnh nhân hôm nay',
                            value: stats.todayPatients,
                            icon: 'fa-users',
                            color: '#3b82f6',
                            bgColor: '#dbeafe'
                        },
                        {
                            title: 'Tổng bệnh nhân',
                            value: stats.totalPatients,
                            icon: 'fa-user-injured',
                            color: '#10b981',
                            bgColor: '#d1fae5'
                        },
                        {
                            title: 'Lịện hẹn chờ',
                            value: stats.pendingAppointments,
                            icon: 'fa-clock',
                            color: '#f59e0b',
                            bgColor: '#fef3c7'
                        },
                        {
                            title: 'Hoàn thành hôm nay',
                            value: stats.completedToday,
                            icon: 'fa-check-circle',
                            color: '#10b981',
                            bgColor: '#d1fae5'
                        }
                    ].map((stat, index) => (
                        <div key={index} className="col-xl-3 col-md-6 mb-3">
                            <div className="card border-0 rounded-4 h-100" style={{
                                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
                                transition: 'all 0.3s ease'
                            }}
                            onMouseEnter={(e) => {
                                e.currentTarget.style.transform = 'translateY(-5px)';
                                e.currentTarget.style.boxShadow = '0 8px 30px rgba(0, 0, 0, 0.12)';
                            }}
                            onMouseLeave={(e) => {
                                e.currentTarget.style.transform = 'translateY(0)';
                                e.currentTarget.style.boxShadow = '0 4px 20px rgba(0, 0, 0, 0.08)';
                            }}>
                                <div className="card-body p-4">
                                    <div className="d-flex align-items-center">
                                        <div className="rounded-circle d-flex align-items-center justify-content-center me-3" style={{
                                            width: '60px',
                                            height: '60px',
                                            backgroundColor: stat.bgColor
                                        }}>
                                            <i className={`fas ${stat.icon} fs-4`} style={{ color: stat.color }}></i>
                                        </div>
                                        <div>
                                            <div className="h3 fw-bold mb-1" style={{ color: stat.color }}>
                                                {stat.value}
                                            </div>
                                            <div className="small text-muted fw-semibold">{stat.title}</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="row">
                    {/* Today's Appointments */}
                    <div className="col-lg-8 mb-4">
                        <div className="card border-0 rounded-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <div className="d-flex justify-content-between align-items-center">
                                    <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                        <i className="fas fa-calendar-check me-2" style={{ color: '#3b82f6' }}></i>
                                        Lịch hẹn hôm nay
                                    </h5>
                                    <button className="btn btn-primary btn-sm rounded-pill">
                                        <i className="fas fa-plus me-2"></i>Thêm lịch hẹn
                                    </button>
                                </div>
                            </div>
                            <div className="card-body p-0">
                                <div className="table-responsive">
                                    <table className="table table-hover mb-0">
                                        <thead style={{ backgroundColor: '#f8fafc' }}>
                                            <tr>
                                                <th className="border-0 px-4 py-3 fw-semibold">Thời gian</th>
                                                <th className="border-0 px-4 py-3 fw-semibold">Bệnh nhân</th>
                                                <th className="border-0 px-4 py-3 fw-semibold">Loại khám</th>
                                                <th className="border-0 px-4 py-3 fw-semibold">Trạng thái</th>
                                                <th className="border-0 px-4 py-3 fw-semibold">Thao tác</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {appointments.map((appointment) => (
                                                <tr key={appointment.id} style={{
                                                    transition: 'all 0.3s ease'
                                                }}
                                                onMouseEnter={(e) => {
                                                    e.currentTarget.style.backgroundColor = '#f8fafc';
                                                }}
                                                onMouseLeave={(e) => {
                                                    e.currentTarget.style.backgroundColor = 'transparent';
                                                }}>
                                                    <td className="px-4 py-3">
                                                        <div className="fw-semibold" style={{ color: '#1f2937' }}>
                                                            {appointment.time}
                                                        </div>
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <div className="d-flex align-items-center">
                                                            <div className="rounded-circle me-3 d-flex align-items-center justify-content-center" style={{
                                                                width: '40px',
                                                                height: '40px',
                                                                backgroundColor: '#dbeafe'
                                                            }}>
                                                                <i className="fas fa-user" style={{ color: '#3b82f6' }}></i>
                                                            </div>
                                                            <div className="fw-semibold">{appointment.patient}</div>
                                                        </div>
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <span className="text-muted">{appointment.type}</span>
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <span className="badge rounded-pill px-3 py-2" style={{
                                                            backgroundColor: `${getStatusColor(appointment.status)}20`,
                                                            color: getStatusColor(appointment.status),
                                                            fontWeight: '600'
                                                        }}>
                                                            {getStatusText(appointment.status)}
                                                        </span>
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <div className="d-flex gap-2">
                                                            <button className="btn btn-sm btn-outline-primary rounded-pill">
                                                                <i className="fas fa-eye"></i>
                                                            </button>
                                                            <button className="btn btn-sm btn-outline-success rounded-pill">
                                                                <i className="fas fa-check"></i>
                                                            </button>
                                                        </div>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Quick Actions & Notifications */}
                    <div className="col-lg-4">
                        {/* Quick Actions */}
                        <div className="card border-0 rounded-4 mb-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-bolt me-2" style={{ color: '#f59e0b' }}></i>
                                    Thao tác nhanh
                                </h5>
                            </div>
                            <div className="card-body p-4">
                                <div className="d-grid gap-3">
                                    {[
                                        { icon: 'fa-user-plus', text: 'Thêm bệnh nhân mới', color: '#3b82f6' },
                                        { icon: 'fa-prescription-bottle-alt', text: 'Kê đơn thuốc', color: '#10b981' },
                                        { icon: 'fa-file-medical', text: 'Xem hồ sơ bệnh án', color: '#6366f1' },
                                        { icon: 'fa-chart-line', text: 'Báo cáo thống kê', color: '#f59e0b' }
                                    ].map((action, index) => (
                                        <button key={index} className="btn btn-light text-start p-3 rounded-3" style={{
                                            border: '2px solid #f1f5f9',
                                            transition: 'all 0.3s ease'
                                        }}
                                        onMouseEnter={(e) => {
                                            e.target.style.borderColor = action.color;
                                            e.target.style.backgroundColor = `${action.color}10`;
                                        }}
                                        onMouseLeave={(e) => {
                                            e.target.style.borderColor = '#f1f5f9';
                                            e.target.style.backgroundColor = '#f8f9fa';
                                        }}>
                                            <i className={`fas ${action.icon} me-3`} style={{ color: action.color }}></i>
                                            <span className="fw-semibold">{action.text}</span>
                                        </button>
                                    ))}
                                </div>
                            </div>
                        </div>

                        {/* Recent Notifications */}
                        <div className="card border-0 rounded-4" style={{
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)'
                        }}>
                            <div className="card-header bg-white border-0 rounded-top-4 p-4">
                                <h5 className="fw-bold mb-0" style={{ color: '#1f2937' }}>
                                    <i className="fas fa-bell me-2" style={{ color: '#ef4444' }}></i>
                                    Thông báo mới
                                </h5>
                            </div>
                            <div className="card-body p-4">
                                <div className="d-flex flex-column gap-3">
                                    {[
                                        { 
                                            icon: 'fa-calendar-plus', 
                                            text: 'Lịch hẹn mới từ Nguyễn Văn A', 
                                            time: '10 phút trước',
                                            color: '#3b82f6'
                                        },
                                        { 
                                            icon: 'fa-user-check', 
                                            text: 'Bệnh nhân đã check-in', 
                                            time: '15 phút trước',
                                            color: '#10b981'
                                        },
                                        { 
                                            icon: 'fa-clipboard-check', 
                                            text: 'Kết quả xét nghiệm đã có', 
                                            time: '1 giờ trước',
                                            color: '#f59e0b'
                                        }
                                    ].map((notification, index) => (
                                        <div key={index} className="d-flex align-items-start p-3 rounded-3" style={{
                                            backgroundColor: '#f8fafc',
                                            border: '1px solid #e2e8f0',
                                            transition: 'all 0.3s ease'
                                        }}
                                        onMouseEnter={(e) => {
                                            e.currentTarget.style.backgroundColor = '#f1f5f9';
                                            e.currentTarget.style.borderColor = notification.color;
                                        }}
                                        onMouseLeave={(e) => {
                                            e.currentTarget.style.backgroundColor = '#f8fafc';
                                            e.currentTarget.style.borderColor = '#e2e8f0';
                                        }}>
                                            <div className="rounded-circle me-3 d-flex align-items-center justify-content-center" style={{
                                                width: '35px',
                                                height: '35px',
                                                backgroundColor: `${notification.color}20`
                                            }}>
                                                <i className={`fas ${notification.icon} small`} style={{ color: notification.color }}></i>
                                            </div>
                                            <div className="flex-grow-1">
                                                <div className="fw-semibold small mb-1" style={{ color: '#1f2937' }}>
                                                    {notification.text}
                                                </div>
                                                <div className="text-muted" style={{ fontSize: '0.8rem' }}>
                                                    {notification.time}
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Custom Styles */}
            <style jsx>{`
                .table-hover tbody tr:hover {
                    background-color: #f8fafc !important;
                }
                
                .btn:focus {
                    box-shadow: none;
                }
                
                .card {
                    border: 1px solid rgba(0, 0, 0, 0.05);
                }
            `}</style>
        </div>
    );
};

export default DoctorDashboard;
