import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ForgetPassword = () => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const navigate = useNavigate();

    const handleReset = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch(`http://localhost:9999/AccountPatient?username=${username}&email=${email}&phone=${phone}`);
            const data = await res.json();

            if (data.length === 1) {
                const account = data[0];
                const updateRes = await fetch(`http://localhost:9999/AccountPatient/${account.id}`, {
                    method: "PATCH",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ password: newPassword })
                });

                if (!updateRes.ok) throw new Error("Cập nhật mật khẩu thất bại");
                alert("Đặt lại mật khẩu thành công!");
                navigate("/login");
            } else {
                alert("Không tìm thấy tài khoản phù hợp với thông tin đã nhập");
            }
        } catch (err) {
            alert("Có lỗi xảy ra khi đặt lại mật khẩu: " + err.message);
        }
    };

    return (
        <div className="forget-password-form">
            <form onSubmit={handleReset}>
                <h2>Quên mật khẩu</h2>
                <label>Tên đăng nhập</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)} required />

                <label>Email</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

                <label>Số điện thoại</label>
                <input value={phone} onChange={(e) => setPhone(e.target.value)} required />

                <label>Mật khẩu mới</label>
                <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />

                <button type="submit">Đặt lại mật khẩu</button>
            </form>
        </div>
    );
};

export default ForgetPassword;
