import { useState, useEffect } from "react";

const sampleUserData = {
  AccountPatient: [
    {
      id: 1,
      username: "phamvannha",
      email: "pham.nha@gmail.com",
      status: "Enable",
    },
    // ... more patient data
  ],
  AccountStaff: [
    {
      id: 1,
      username: "doquocdat",
      email: "dat.doctor01@health.vn",
      role: "Doctor",
      status: "Enable",
    },
    // ... more staff data
  ],
};

// Component to edit user information
function EditUserForm() {
  const [userType, setUserType] = useState("patient"); // 'patient' or 'staff'
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    status: "Enable",
    role: "Doctor", // Only for staff
  });
  const [error, setError] = useState("");

  // Simulate fetching user data based on ID (e.g., from URL or prop)
  useEffect(() => {
    // Example: Fetch user with ID 1, type 'patient'
    const userId = 1; // Replace with dynamic ID from props or URL
    const type = "patient"; // Replace with dynamic type
    setUserType(type);

    let fetchedUser;
    if (type === "patient") {
      fetchedUser = sampleUserData.AccountPatient.find((u) => u.id === userId);
    } else {
      fetchedUser = sampleUserData.AccountStaff.find((u) => u.id === userId);
    }

    if (fetchedUser) {
      setUser(fetchedUser);
      setFormData({
        username: fetchedUser.username,
        email: fetchedUser.email,
        status: fetchedUser.status,
        role: fetchedUser.role || "Doctor",
      });
    } else {
      setError("User not found");
    }
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Basic validation
    if (!formData.username || !formData.email) {
      setError("Username and email are required");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      setError("Invalid email format");
      return;
    }

    // Simulate saving data
    console.log("Saving user data:", {
      ...formData,
      id: user?.id,
      type: userType,
    });
    alert("User information updated successfully!");
    setError("");
  };

  if (error) {
    return (
      <div className="max-w-lg mx-auto mt-10 p-6 bg-red-50 border-l-4 border-red-500 rounded-lg shadow-lg">
        <p className="text-red-700 font-semibold text-center">{error}</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="max-w-lg mx-auto mt-10 p-6 bg-gray-50 rounded-lg shadow-lg">
        <p className="text-gray-600 font-medium text-center animate-pulse">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-gray-100 flex items-center justify-center py-10">
      <div className="max-w-lg w-full bg-white p-8 rounded-xl shadow-2xl">
        <div className="flex items-center justify-center mb-6">
          <svg className="w-8 h-8 text-blue-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"></path>
          </svg>
          <h2 className="text-3xl font-bold text-gray-800">
            Edit {userType === "patient" ? "Patient" : "Staff"} Information
          </h2>
        </div>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Username</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-500 transition duration-200 bg-gray-50"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-500 transition duration-200 bg-gray-50"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Status</label>
            <select
              name="status"
              value={formData.status}
              onChange={handleChange}
              className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-500 transition duration-200 bg-gray-50"
            >
              <option value="Enable">Enable</option>
              <option value="Disable">Disable</option>
            </select>
          </div>
          {userType === "staff" && (
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">Role</label>
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-500 transition duration-200 bg-gray-50"
              >
                <option value="Doctor">Doctor</option>
                <option value="Nurse">Nurse</option>
                <option value="Receptionist">Receptionist</option>
                <option value="AdminSys">AdminSys</option>
                <option value="AdminBusiness">AdminBusiness</option>
              </select>
            </div>
          )}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 focus:ring-4 focus:ring-blue-300 transition duration-200 font-semibold"
          >
            Save Changes
          </button>
        </form>
      </div>
    </div>
  );
}

export default EditUserForm;
