import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./RegisterPatient.css";

const RegisterPatient = () => {
  const navigate = useNavigate();
  const [account, setAccount] = useState({
    username: "",
    password: "",
    email: "",
  });

  const [showPassword, setShowPassword] = useState(false);

  const [patient, setPatient] = useState({
    full_name: "",
    dob: "",
    gender: "Nam",
    phone: "",
    address: "",
  });

  const [errors, setErrors] = useState({});

  const validateField = (name, value) => {
    let message = "";

    switch (name) {
      case "username":
        if (!value.trim()) message = "Vui l√≤ng nh·∫≠p t√™n ƒëƒÉng nh·∫≠p";
        break;
      case "password":
        if (!value || value.length < 6) message = "M·∫≠t kh·∫©u t·ªëi thi·ªÉu 6 k√Ω t·ª±";
        else if (value.includes(" ")) message = "M·∫≠t kh·∫©u kh√¥ng ƒë∆∞·ª£c ch·ª©a d·∫•u c√°ch";
        break;
      case "email":
        if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(value)) message = "Email kh√¥ng h·ª£p l·ªá";
        break;
      case "full_name":
      case "address":
        if (!value.trim()) message = `Vui l√≤ng nh·∫≠p ${name === "full_name" ? "h·ªç t√™n" : "ƒë·ªãa ch·ªâ"}`;
        else if (value.length < 2 || value.length > 50) message = `${name === "full_name" ? "H·ªç t√™n" : "ƒê·ªãa ch·ªâ"} ph·∫£i t·ª´ 2 ƒë·∫øn 50 k√Ω t·ª±`;
        else if (!/^[A-Za-z√Ä-·ªπ\s]+$/.test(value)) message = `${name === "full_name" ? "H·ªç t√™n" : "ƒê·ªãa ch·ªâ"} ch·ªâ ƒë∆∞·ª£c ch·ª©a ch·ªØ c√°i v√† d·∫•u c√°ch`;
        else if (!/^[A-Za-z√Ä-·ªπ]/.test(value)) message = `K√Ω t·ª± ƒë·∫ßu ti√™n c·ªßa ${name === "full_name" ? "h·ªç t√™n" : "ƒë·ªãa ch·ªâ"} ph·∫£i l√† ch·ªØ c√°i`;
        else if (/(.)\1\1/.test(value)) message = `${name === "full_name" ? "H·ªç t√™n" : "ƒê·ªãa ch·ªâ"} kh√¥ng ƒë∆∞·ª£c ch·ª©a 3 k√Ω t·ª± gi·ªëng nhau li√™n ti·∫øp`;
        break;
      case "dob":
        if (!value) message = "Vui l√≤ng ch·ªçn ng√†y sinh";
        else if (new Date(value) > new Date()) message = "Ng√†y sinh kh√¥ng ƒë∆∞·ª£c l·ªõn h∆°n ng√†y hi·ªán t·∫°i";
        break;
      case "phone":
        if (!/^0[0-9]{9}$/.test(value)) message = "S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá (b·∫Øt ƒë·∫ßu b·∫±ng 0, ƒë·ªß 10 s·ªë)";
        break;
      default:
        break;
    }

    setErrors((prev) => ({ ...prev, [name]: message }));
  };

  const handleAccountChange = (e) => {
    const { name, value } = e.target;
    setAccount({ ...account, [name]: value });
    validateField(name, value);
  };

  const handlePatientChange = (e) => {
    const { name, value } = e.target;
    setPatient({ ...patient, [name]: value });
    validateField(name, value);
  };

  const validate = () => {
    const allFields = { ...account, ...patient };
    Object.entries(allFields).forEach(([name, value]) => {
      validateField(name, value);
    });
    return Object.values(errors).every((err) => !err);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const accRes = await fetch("http://localhost:3001/AccountPatient", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...account,
          img: "default.png",
          status: "Enable",
        }),
      });

      if (!accRes.ok) throw new Error("T·∫°o t√†i kho·∫£n th·∫•t b·∫°i");
      const accData = await accRes.json();

      const patientRes = await fetch("http://localhost:3001/Patient", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...patient,
          id: accData.id,
        }),
      });

      if (!patientRes.ok) throw new Error("L∆∞u th√¥ng tin b·ªánh nh√¢n th·∫•t b·∫°i");

      alert("ƒêƒÉng k√Ω th√†nh c√¥ng!");
      navigate("/login");
    } catch (err) {
      alert("ƒêƒÉng k√Ω th·∫•t b·∫°i!" + err.message);
    }
  };

  return (
    <div className="register-form-container">
      <form className="register-form" onSubmit={handleSubmit}>
        <h2>ƒêƒÉng k√Ω t√†i kho·∫£n b·ªánh nh√¢n</h2>

        <label>T√™n ƒëƒÉng nh·∫≠p</label>
        <input name="username" onChange={handleAccountChange} />
        {errors.username && <span className="error">{errors.username}</span>}

        <label>M·∫≠t kh·∫©u</label>
        <div style={{ position: "relative" }}>
          <input
            type={showPassword ? "text" : "password"}
            name="password"
            onChange={handleAccountChange}
          />
          <span
            style={{
              position: "absolute",
              right: 10,
              top: "50%",
              transform: "translateY(-50%)",
              cursor: "pointer",
            }}
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? "üôà" : "üëÅÔ∏è"}
          </span>
        </div>
        {errors.password && <span className="error">{errors.password}</span>}

        <label>Email</label>
        <input type="email" name="email" onChange={handleAccountChange} />
        {errors.email && <span className="error">{errors.email}</span>}

        <label>H·ªç v√† t√™n</label>
        <input name="full_name" onChange={handlePatientChange} />
        {errors.full_name && <span className="error">{errors.full_name}</span>}

        <label>Ng√†y sinh</label>
        <input type="date" name="dob" onChange={handlePatientChange} />
        {errors.dob && <span className="error">{errors.dob}</span>}

        <label>Gi·ªõi t√≠nh</label>
        <select name="gender" onChange={handlePatientChange}>
          <option value="Nam">Nam</option>
          <option value="N·ªØ">N·ªØ</option>
          <option value="Kh√°c">Kh√°c</option>
        </select>

        <label>S·ªë ƒëi·ªán tho·∫°i</label>
        <input name="phone" onChange={handlePatientChange} />
        {errors.phone && <span className="error">{errors.phone}</span>}

        <label>ƒê·ªãa ch·ªâ</label>
        <input name="address" onChange={handlePatientChange} />
        {errors.address && <span className="error">{errors.address}</span>}

        <button type="submit">T·∫°o t√†i kho·∫£n</button>
      </form>
    </div>
  );
};

export default RegisterPatient;
