import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditService = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [form, setForm] = useState({ name: '', price: '', description: '' });

    useEffect(() => {
        fetch(`http://localhost:9999/ListOfMedicalService/${id}`)
            .then((res) => res.json())
            .then((data) => setForm(data));
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await fetch(`http://localhost:9999/ListOfMedicalService/${id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form)
        });
        alert('Cập nhật thành công!');
        navigate('/services');
    };

    return (
        <div className="container py-5">
            <h2 className="mb-4 text-center">Chỉnh sửa dịch vụ</h2>
            <form className="card p-4 shadow-sm" onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Tên dịch vụ</label>
                    <input className="form-control" name="name" value={form.name} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label className="form-label">Giá (VNĐ)</label>
                    <input className="form-control" name="price" type="number" value={form.price} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label className="form-label">Mô tả</label>
                    <textarea className="form-control" name="description" value={form.description} onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-success w-100">Lưu thay đổi</button>
            </form>
        </div>
    );
};

export default EditService;
