import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DoctorHeader from './DoctorHeader';
import { 
    getWaitlistByDoctorId, 
    getPatients, 
    getRooms,
    createExamResult,
    updateWaitlistStatus,
    getMedicineRecordByPatientId,
    createMedicineRecord,
    getExamResults,
    getExamResultsByDoctorId,
    getExamResultByMedicineRecordId,
    getMedicineRecords,
    getServiceOrdersByDoctorId,
    getServiceOrderItems,
    getResultsOfParaclinicalServices,
    getMedicalServices,
    getDiagnosisByMedicineRecordId,
    createDiagnosis
} from '../services/api';

const DoctorDashboard = () => {
    const navigate = useNavigate();
    
    const [waitlist, setWaitlist] = useState([]);
    const [allWaitlist, setAllWaitlist] = useState([]); // For statistics
    const [patients, setPatients] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [examResults, setExamResults] = useState([]);
    const [medicineRecords, setMedicineRecords] = useState([]);
    const [serviceOrders, setServiceOrders] = useState([]);
    const [serviceOrderItems, setServiceOrderItems] = useState([]);
    const [serviceResults, setServiceResults] = useState([]);
    const [medicalServices, setMedicalServices] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentDoctorId, setCurrentDoctorId] = useState('1'); // Mock doctor ID - should come from auth
    
    // Exam Modal States
    const [showExamModal, setShowExamModal] = useState(false);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [selectedWaitlistItem, setSelectedWaitlistItem] = useState(null);
    const [examForm, setExamForm] = useState({
        symptoms: '',
        preliminary_diagnosis: '',
        notes: ''
    });
    const [submitting, setSubmitting] = useState(false);

    // Diagnosis Modal States
    const [showDiagnosisModal, setShowDiagnosisModal] = useState(false);
    const [selectedServiceOrder, setSelectedServiceOrder] = useState(null);
    const [diagnosisForm, setDiagnosisForm] = useState({
        conclusion: '',
        disease: '',
        treatment_plan: ''
    });
    const [diagnosisSubmitting, setDiagnosisSubmitting] = useState(false);

    // Fetch data on component mount
    useEffect(() => {
        fetchDashboardData();
    }, [currentDoctorId]);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            const [waitlistRes, patientsRes, roomsRes, examResultsRes, medicineRecordsRes, serviceOrdersRes, serviceOrderItemsRes, serviceResultsRes, medicalServicesRes] = await Promise.all([
                getWaitlistByDoctorId(currentDoctorId),
                getPatients(),
                getRooms(),
                getExamResultsByDoctorId(currentDoctorId),
                getMedicineRecords(),
                getServiceOrdersByDoctorId(currentDoctorId),
                getServiceOrderItems(),
                getResultsOfParaclinicalServices(),
                getMedicalServices()
            ]);

            if (waitlistRes.success) {
                // Store all waitlist for statistics and display
                setAllWaitlist(waitlistRes.data);
                
                // Show all waitlist for this doctor (already filtered by doctor_id in API call)
                setWaitlist(waitlistRes.data);
            }

            if (examResultsRes.success) {
                console.log('ExamResults for doctor', currentDoctorId, ':', examResultsRes.data);
                setExamResults(examResultsRes.data);
            }

            if (medicineRecordsRes.success) {
                setMedicineRecords(medicineRecordsRes.data);
            }

            if (patientsRes.success) {
                setPatients(patientsRes.data);
            }

            if (roomsRes.success) {
                setRooms(roomsRes.data);
            }

            if (serviceOrdersRes.success) {
                setServiceOrders(serviceOrdersRes.data);
            }

            if (serviceOrderItemsRes.success) {
                setServiceOrderItems(serviceOrderItemsRes.data);
            }

            if (serviceResultsRes.success) {
                setServiceResults(serviceResultsRes.data);
            }

            if (medicalServicesRes.success) {
                setMedicalServices(medicalServicesRes.data);
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            alert('Có lỗi xảy ra khi tải dữ liệu!');
        } finally {
            setLoading(false);
        }
    };

    // Get patient info by ID
    const getPatientInfo = (patientId) => {
        return patients.find(p => p.id === patientId) || {};
    };

    // Get room info by ID
    const getRoomInfo = (roomId) => {
        return rooms.find(r => r.id === roomId) || {};
    };

    // Format time display
    const formatTime = (timeString) => {
        return new Date(timeString).toLocaleTimeString('vi-VN', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    // Handle start examination
    const handleStartExam = (waitlistItem, isAssignment = false) => {
        const patient = getPatientInfo(waitlistItem.patient_id);
        
        if (isAssignment) {
            // Navigate to service assignment page
            navigate(`/service-assignment/${waitlistItem.patient_id}/${waitlistItem.id}`);
        } else {
            // Open exam modal
            setSelectedWaitlistItem(waitlistItem);
            setSelectedPatient(patient);
            setShowExamModal(true);
            setExamForm({
                symptoms: '',
                preliminary_diagnosis: '',
                notes: ''
            });
        }
    };

    // Handle exam form submission
    const handleExamSubmit = async (e) => {
        e.preventDefault();
        
        if (!examForm.symptoms.trim() || !examForm.preliminary_diagnosis.trim()) {
            alert('Vui lòng nhập đầy đủ triệu chứng và chẩn đoán sơ bộ!');
            return;
        }

        setSubmitting(true);

        try {
            // First, get or create medicine record for the patient
            let medicineRecordRes = await getMedicineRecordByPatientId(selectedPatient.id);
            let medicineRecordId;

            if (medicineRecordRes.success && medicineRecordRes.data.length > 0) {
                medicineRecordId = medicineRecordRes.data[0].id;
            } else {
                // Create new medicine record
                const newRecordRes = await createMedicineRecord({
                    patient_id: selectedPatient.id
                });
                if (newRecordRes.success) {
                    medicineRecordId = newRecordRes.data.id;
                } else {
                    throw new Error('Không thể tạo hồ sơ bệnh án');
                }
            }

            // Create exam result
            const examData = {
                medicineRecord_id: medicineRecordId,
                symptoms: examForm.symptoms,
                preliminary_diagnosis: examForm.preliminary_diagnosis,
                doctor_id: currentDoctorId,
                notes: examForm.notes || ''
            };

            const examResultRes = await createExamResult(examData);
            
            if (examResultRes.success) {
                // Update waitlist status to InProgress
                const updateRes = await updateWaitlistStatus(selectedWaitlistItem.id, 'InProgress');
                
                if (updateRes.success) {
                    alert('✅ Đã lưu kết quả khám và cập nhật trạng thái thành công!');
                    setShowExamModal(false);
                    fetchDashboardData(); // Refresh all data including exam results by doctor
                } else {
                    alert('Đã lưu kết quả khám nhưng không thể cập nhật trạng thái!');
                }
            } else {
                throw new Error('Không thể lưu kết quả khám');
            }

        } catch (error) {
            console.error('Error saving exam result:', error);
            alert('❌ Có lỗi xảy ra khi lưu kết quả khám: ' + error.message);
        } finally {
            setSubmitting(false);
        }
    };

    // Get visit type text in Vietnamese
    const getVisitTypeText = (visitType) => {
        switch(visitType) {
            case 'Initial': return 'Khám lần đầu';
            case 'Result': return 'Xem kết quả';
            default: return visitType;
        }
    };

    // Check if patient has exam result
    const hasExamResult = (patientId) => {
        const medicineRecord = medicineRecords.find(record => record.patient_id === patientId);
        if (!medicineRecord) return false;
        
        const examResult = examResults.find(result => result.medicineRecord_id === medicineRecord.id);
        return !!examResult;
    };

    // Get exam result for patient
    const getExamResultForPatient = (patientId) => {
        const medicineRecord = medicineRecords.find(record => record.patient_id === patientId);
        if (!medicineRecord) return null;
        
        return examResults.find(result => result.medicineRecord_id === medicineRecord.id);
    };

    // Refresh only exam results for better performance
    const refreshExamResults = async () => {
        try {
            const examResultsRes = await getExamResultsByDoctorId(currentDoctorId);
            if (examResultsRes.success) {
                setExamResults(examResultsRes.data);
            }
        } catch (error) {
            console.error('Error refreshing exam results:', error);
        }
    };

    if (loading) {
        return (
            <div className="container-fluid py-5">
                <div className="text-center">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Đang tải...</span>
                    </div>
                    <p className="mt-3 text-muted">Đang tải dữ liệu...</p>
                </div>
            </div>
        );
    }

    return (
        <>
            <DoctorHeader />
            <div className="container-fluid py-4">
                {/* Dashboard Header */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                    <div>
                        <h2 className="fw-bold text-dark mb-1">
                            <i className="fas fa-chart-line text-primary me-2"></i>
                            Dashboard
                        </h2>
                        <p className="text-muted mb-0">Tổng quan về hoạt động khám chữa bệnh</p>
                    </div>
                    <button 
                        className="btn btn-outline-primary"
                        onClick={fetchDashboardData}
                        disabled={loading}
                    >
                        <i className="fas fa-sync-alt me-2"></i>
                        Làm mới
                    </button>
                </div>

            {/* Statistics Cards */}
            <div className="row mb-4">
                <div className="col-md-3">
                    <div className="card border-0 shadow-sm">
                        <div className="card-body text-center">
                            <div className="text-primary fs-1 mb-2">
                                <i className="fas fa-clock"></i>
                            </div>
                            <h3 className="fw-bold text-primary">{waitlist.length}</h3>
                            <p className="text-muted mb-0">Tổng bệnh nhân</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card border-0 shadow-sm">
                        <div className="card-body text-center">
                            <div className="text-success fs-1 mb-2">
                                <i className="fas fa-stethoscope"></i>
                            </div>
                            <h3 className="fw-bold text-success">
                                {waitlist.filter(item => item.status === 'Waiting').length}
                            </h3>
                            <p className="text-muted mb-0">Đang chờ khám</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card border-0 shadow-sm">
                        <div className="card-body text-center">
                            <div className="text-warning fs-1 mb-2">
                                <i className="fas fa-file-medical"></i>
                            </div>
                            <h3 className="fw-bold text-warning">
                                {waitlist.filter(item => item.status === 'InProgress').length}
                            </h3>
                            <p className="text-muted mb-0">Đang điều trị</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card border-0 shadow-sm">
                        <div className="card-body text-center">
                            <div className="text-info fs-1 mb-2">
                                <i className="fas fa-calendar-day"></i>
                            </div>
                            <h3 className="fw-bold text-info">
                                {new Date().toLocaleDateString('vi-VN')}
                            </h3>
                            <p className="text-muted mb-0">Hôm nay</p>
                        </div>
                    </div>
                </div>
            </div>

            {/* Waitlist Table */}
            <div className="card border-0 shadow-sm">
                <div className="card-header bg-primary text-white">
                    <h5 className="mb-0">
                        <i className="fas fa-list me-2"></i>
                        Danh Sách Bệnh Nhân Chờ Khám
                    </h5>
                </div>
                <div className="card-body p-0">
                    {waitlist.length === 0 ? (
                        <div className="text-center py-5">
                            <i className="fas fa-clipboard-list fs-1 text-muted mb-3"></i>
                            <h5 className="text-muted">Không có bệnh nhân nào trong danh sách chờ</h5>
                            <p className="text-muted">Danh sách tất cả bệnh nhân được phân công cho bác sĩ này</p>
                        </div>
                    ) : (
                        <div className="table-responsive">
                            <table className="table table-hover mb-0">
                                <thead className="table-light">
                                    <tr>
                                        <th>STT</th>
                                        <th>Thông tin bệnh nhân</th>
                                        <th>Phòng khám</th>
                                        <th>Thời gian đăng ký</th>
                                        <th>Thời gian dự kiến</th>
                                        <th>Loại khám</th>
                                        <th>Thao tác</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {waitlist.map((item, index) => {
                                        const patient = getPatientInfo(item.patient_id);
                                        const room = getRoomInfo(item.room_id);
                                        
                                        return (
                                            <tr key={item.id}>
                                                <td className="fw-bold text-primary">{index + 1}</td>
                                                <td>
                                                    <div>
                                                        <div className="fw-semibold">{patient.full_name}</div>
                                                        <small className="text-muted">
                                                            {patient.gender} • {patient.phone}
                                                        </small>
                                                    </div>
                                                </td>
                                                <td>
                                                    <div>
                                                        <div className="fw-semibold">{room.room_name}</div>
                                                        <small className="text-muted">{room.department}</small>
                                                    </div>
                                                </td>
                                                <td>{formatTime(item.registered_at)}</td>
                                                <td>
                                                    <span className="badge bg-info">
                                                        {formatTime(item.estimated_time)}
                                                    </span>
                                                </td>
                                                <td>
                                                    <span className={`badge ${item.visittype === 'Initial' ? 'bg-success' : 'bg-warning'}`}>
                                                        {getVisitTypeText(item.visittype)}
                                                    </span>
                                                </td>
                                                <td>
                                                    {hasExamResult(item.patient_id) ? (
                                                        <button
                                                            className="btn btn-warning btn-sm"
                                                            onClick={() => handleStartExam(item, true)}
                                                            title="Chỉ định dịch vụ điều trị"
                                                        >
                                                            <i className="fas fa-clipboard-list me-1"></i>
                                                            Chỉ định
                                                        </button>
                                                    ) : (
                                                        <button
                                                            className="btn btn-primary btn-sm"
                                                            onClick={() => handleStartExam(item, false)}
                                                            title="Bắt đầu khám bệnh"
                                                        >
                                                            <i className="fas fa-stethoscope me-1"></i>
                                                            Khám
                                                        </button>
                                                    )}
                                                </td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {/* Exam Results Section */}
            <div className="row mt-4">
                <div className="col-12">
                    <div className="card border-0 shadow-sm">
                        <div className="card-header bg-success text-white">
                            <h5 className="mb-0">
                                <i className="fas fa-file-medical me-2"></i>
                                Kết Quả Khám Bệnh Của Tôi
                            </h5>
                            <p className="mb-0 small">Danh sách bệnh nhân đã khám bởi bác sĩ hiện tại</p>
                        </div>
                        
                        {examResults.length === 0 ? (
                            <div className="card-body text-center py-5">
                                <i className="fas fa-file-medical-alt fa-3x text-muted mb-3"></i>
                                <h5 className="text-muted">Chưa có kết quả khám nào</h5>
                                <p className="text-muted">Các kết quả khám sẽ xuất hiện ở đây sau khi hoàn thành</p>
                            </div>
                        ) : (
                            <div className="table-responsive">
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>Bệnh nhân</th>
                                            <th>Triệu chứng</th>
                                            <th>Chẩn đoán sơ bộ</th>
                                            <th>Thời gian khám</th>
                                            <th>Thao tác</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {examResults.map((result) => {
                                            const medicineRecord = medicineRecords.find(record => record.id === result.medicineRecord_id);
                                            const patient = medicineRecord ? getPatientInfo(medicineRecord.patient_id) : null;
                                            
                                            return (
                                                <tr key={result.id}>
                                                    <td>
                                                        <div className="d-flex align-items-center">
                                                            <div className="avatar bg-success text-white rounded-circle me-3 d-flex align-items-center justify-content-center" style={{width: '40px', height: '40px'}}>
                                                                <i className="fas fa-user"></i>
                                                            </div>
                                                            <div>
                                                                <strong>{patient?.full_name || 'N/A'}</strong>
                                                                <br />
                                                                <small className="text-muted">{patient?.phone || 'N/A'}</small>
                                                            </div>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <span className="text-wrap" style={{maxWidth: '200px', display: 'inline-block'}}>
                                                            {result.symptoms}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <span className="text-wrap" style={{maxWidth: '200px', display: 'inline-block'}}>
                                                            {result.preliminary_diagnosis}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <span className="badge bg-info">
                                                            Vừa hoàn thành
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <button
                                                            className="btn btn-outline-primary btn-sm me-2"
                                                            title="Xem chi tiết"
                                                        >
                                                            <i className="fas fa-eye"></i>
                                                        </button>
                                                        <button
                                                            className="btn btn-outline-secondary btn-sm"
                                                            title="In kết quả"
                                                        >
                                                            <i className="fas fa-print"></i>
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Exam Modal */}
            {showExamModal && (
                <div className="modal fade show d-block" tabIndex="-1" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header bg-primary text-white">
                                <h5 className="modal-title">
                                    <i className="fas fa-stethoscope me-2"></i>
                                    Khám Bệnh Lâm Sàng
                                </h5>
                                <button 
                                    type="button" 
                                    className="btn-close btn-close-white" 
                                    onClick={() => setShowExamModal(false)}
                                    disabled={submitting}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {/* Patient Info */}
                                <div className="alert alert-info">
                                    <h6 className="mb-2">
                                        <i className="fas fa-user me-2"></i>
                                        Thông tin bệnh nhân
                                    </h6>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <strong>Họ tên:</strong> {selectedPatient?.full_name}
                                        </div>
                                        <div className="col-md-6">
                                            <strong>Giới tính:</strong> {selectedPatient?.gender}
                                        </div>
                                        <div className="col-md-6">
                                            <strong>Ngày sinh:</strong> {selectedPatient?.dob}
                                        </div>
                                        <div className="col-md-6">
                                            <strong>Điện thoại:</strong> {selectedPatient?.phone}
                                        </div>
                                    </div>
                                </div>

                                {/* Exam Form */}
                                <form onSubmit={handleExamSubmit}>
                                    <div className="row">
                                        <div className="col-12 mb-3">
                                            <label className="form-label fw-semibold">
                                                <i className="fas fa-thermometer-half me-2 text-danger"></i>
                                                Triệu chứng <span className="text-danger">*</span>
                                            </label>
                                            <textarea
                                                className="form-control"
                                                rows="4"
                                                placeholder="Mô tả các triệu chứng của bệnh nhân..."
                                                value={examForm.symptoms}
                                                onChange={(e) => setExamForm(prev => ({...prev, symptoms: e.target.value}))}
                                                required
                                                disabled={submitting}
                                            />
                                        </div>

                                        <div className="col-12 mb-3">
                                            <label className="form-label fw-semibold">
                                                <i className="fas fa-diagnoses me-2 text-primary"></i>
                                                Chẩn đoán sơ bộ <span className="text-danger">*</span>
                                            </label>
                                            <textarea
                                                className="form-control"
                                                rows="3"
                                                placeholder="Nhập chẩn đoán sơ bộ..."
                                                value={examForm.preliminary_diagnosis}
                                                onChange={(e) => setExamForm(prev => ({...prev, preliminary_diagnosis: e.target.value}))}
                                                required
                                                disabled={submitting}
                                            />
                                        </div>

                                        <div className="col-12 mb-3">
                                            <label className="form-label fw-semibold">
                                                <i className="fas fa-sticky-note me-2 text-warning"></i>
                                                Ghi chú thêm
                                            </label>
                                            <textarea
                                                className="form-control"
                                                rows="2"
                                                placeholder="Ghi chú thêm (tùy chọn)..."
                                                value={examForm.notes}
                                                onChange={(e) => setExamForm(prev => ({...prev, notes: e.target.value}))}
                                                disabled={submitting}
                                            />
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary" 
                                    onClick={() => setShowExamModal(false)}
                                    disabled={submitting}
                                >
                                    <i className="fas fa-times me-2"></i>
                                    Hủy
                                </button>
                                <button 
                                    type="submit" 
                                    className="btn btn-primary"
                                    onClick={handleExamSubmit}
                                    disabled={submitting}
                                >
                                    {submitting ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2"></span>
                                            Đang lưu...
                                        </>
                                    ) : (
                                        <>
                                            <i className="fas fa-save me-2"></i>
                                            Lưu kết quả khám
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
        </>
    );
};

export default DoctorDashboard;
