import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

const EditPatient = ({ patientId , onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    full_name: '',
    dob: '',
    gender: '',
    phone: '',
    address: '',
    img: null, // Store image file
    imgPreview: 'default.png', // Preview URL or base64
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch patient and account data when component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Fetch Patient data
        const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`);
        if (!patientResponse.ok) {
          throw new Error('Không thể tải thông tin bệnh nhân');
        }
        const patientData = await patientResponse.json();

        // Fetch AccountPatient data (for img)
        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`);
        if (!accountResponse.ok) {
          throw new Error('Không thể tải thông tin tài khoản');
        }
        const accountData = await accountResponse.json();

        setFormData({
          full_name: patientData.full_name || '',
          dob: patientData.dob || '',
          gender: patientData.gender || '',
          phone: patientData.phone || '',
          address: patientData.address || '',
          img: null,
          imgPreview: accountData.img || 'default.png',
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [patientId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prev) => ({
        ...prev,
        img: file,
        imgPreview: URL.createObjectURL(file),
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Update Patient data
      const patientResponse = await fetch(`http://localhost:9999/Patient/${patientId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: patientId,
          full_name: formData.full_name,
          dob: formData.dob,
          gender: formData.gender,
          phone: formData.phone,
          address: formData.address,
        }),
      });

      if (!patientResponse.ok) {
        throw new Error('Không thể cập nhật thông tin bệnh nhân');
      }

      // Update AccountPatient image if a new image is selected
      if (formData.img) {
        const formDataToSend = new FormData();
        formDataToSend.append('img', formData.img);

        const accountResponse = await fetch(`http://localhost:9999/AccountPatient/${patientId}`, {
          method: 'PUT',
          body: formDataToSend,
        });

        if (!accountResponse.ok) {
          throw new Error('Không thể cập nhật ảnh tài khoản');
        }
      }

      const updatedPatient = await patientResponse.json();
      onSave(updatedPatient);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="text-center p-6">Đang tải...</div>;
  }

  if (error) {
    return <div className="text-center p-6 text-red-500">Lỗi: {error}</div>;
  }

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Chỉnh sửa thông tin bệnh nhân</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="full_name">
            Họ và tên
          </label>
          <input
            type="text"
            id="full_name"
            name="full_name"
            value={formData.full_name}
            onChange={handleChange}
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="dob">
            Ngày sinh
          </label>
          <input
            type="date"
            id="dob"
            name="dob"
            value={formData.dob}
            onChange={handleChange}
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="gender">
            Giới tính
          </label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="">Chọn giới tính</option>
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="phone">
            Số điện thoại
          </label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            pattern="[0-9]{10}"
            placeholder="Nhập số điện thoại (10 số)"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="address">
            Địa chỉ
          </label>
          <textarea
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="4"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor="img">
            Ảnh đại diện
          </label>
          <div className="flex items-center space-x-4">
            <img
              src={formData.imgPreview}
              alt="Ảnh đại diện"
              className="w-24 h-24 object-cover rounded-full border"
            />
            <input
              type="file"
              id="img"
              name="img"
              accept="image/*"
              onChange={handleImageChange}
              className="p-2"
            />
          </div>
        </div>
        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={onCancel}
            className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
            disabled={loading}
          >
            Hủy
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            disabled={loading}
          >
            Lưu
          </button>
        </div>
      </form>
    </div>
  );
};

EditPatient.propTypes = {
  patientId: PropTypes.string.isRequired,
  onSave: PropTypes.func.isRequired,
  onCancel: PropTypes.func.isRequired,
};

export default EditPatient;
