import React from "react";
import { useNavigate } from "react-router-dom";

const AdminBusinessDashboard = () => {
    const user = JSON.parse(localStorage.getItem("user"));
    const navigate = useNavigate();

    if (!user || user.userType !== "staff" || user.role !== "AdminBusiness") {
        navigate("/login");
        return null;
    }


    const features = [
        {
            icon: "üìÑ",
            title: "Danh s√°ch h√≥a ƒë∆°n",
            description: "Xem v√† theo d√µi c√°c h√≥a ƒë∆°n ƒë√£ t·∫°o",
            link: "/invoice-list",
        },
        {
            icon: "üìä",
            title: "Th·ªëng k√™ doanh thu",
            description: "Xem b√°o c√°o doanh thu theo th·ªùi gian",
            link: "/revenue-statistics",
        },
    ];

    return (
        <div className="container py-5">
            <h2 className="text-center text-primary mb-5">
                Xin ch√†o, Qu·∫£n l√Ω t√†i ch√≠nh {user.full_name || ""}
            </h2>
            <div className="row g-4">
                {features.map((f, idx) => (
                    <div className="col-md-6" key={idx}>
                        <div
                            className="card h-100 shadow-sm"
                            onClick={() => navigate(f.link)}
                            style={{ cursor: "pointer" }}
                        >
                            <div className="card-body text-center">
                                <div style={{ fontSize: "2.5rem" }}>{f.icon}</div>
                                <h5 className="card-title mt-3">{f.title}</h5>
                                <p className="card-text text-muted">{f.description}</p>
                            </div>
                            <div className="card-footer text-center bg-transparent">
                                <button className="btn btn-outline-primary btn-sm">Truy c·∫≠p</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminBusinessDashboard;
